import React, {Component} from "react";
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';

const styles = theme => ({
    location: {
        textAlign: 'center',
	width:'100%',
    },
});

//	color: 'white',

class LocationComponent extends Component {
    constructor(props) {
	super(props);
	const {state} = props;
	state.React.Location=this;
    };
    showLocation(state) {
	//console.log("Showing LocationComponent.",JSON.stringify(state.Path.keys));
	this.forceUpdate();
    };
    render() {
        const { classes, state } = this.props;
	var tooltip=state.Path.getTooltip(state);
	//console.log("###### Tooltip:",title);
        return (
            <div className={classes.location}>
     		{tooltip}
            </div>
        );
    }
}

LocationComponent.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(LocationComponent);
