import React, {Component} from "react";
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';

import {teal_palette} from '../mui/metMuiThemes';

import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';

import SummaryCell from './SummaryCell';
import SeriesCell  from './SeriesCell';
import CanvasText  from './CanvasTextComponent';

const styles = theme => ({
    root: {
	height: '100%',
    },
    paper: {
	overflow: 'hidden',
	tableLayout: 'fixed',
	padding:0,
	margin:0,
    },
    divTable :{
	display: 'table',
	width: '100%',
    },
    divTableRow:  {
	display: 'table-row',
    },
    divTableCell:{
	border: '0px solid #999999',
	display: 'table-cell',
	padding: '0px 0px',
    },
    divTableCellCursor:{
	cursor: "pointer",
	border: '0px solid #999999',
	display: 'table-cell',
	padding: '0px 0px',
    },
    divTableHead : {
	border: '0px solid #999999',
	display: 'table-cell',
	padding: '0px 0px',
    },
    divTableHeading : {
	display: 'table-header-group',
    },
    divTableHeadingCenter : {
	display: 'flex',
	justifyContent: 'center',
    },
    divTableFoot : {
	backgroundColor: '#DDD',
	display: 'table-footer-group',
	fontWeight: 'bold',
    },
    divTableBody : {
	display: 'table-row-group',
    }
});

//const mui = createTheme({palette:teal_palette});

function getTextWidth(txt, fontname, fontsize){
    if(getTextWidth.c === undefined){
        getTextWidth.c=document.createElement('canvas');
        getTextWidth.ctx=getTextWidth.c.getContext('2d');
    }
    if (fontname !== undefined) {
	getTextWidth.ctx.font = fontsize + ' ' + fontname;
    }
    return getTextWidth.ctx.measureText(txt).width;
};
function getTextHeight(fontname, fontsize){
    if(getTextHeight.c === undefined){
        getTextHeight.c=document.createElement('canvas');
        getTextHeight.ctx=getTextHeight.c.getContext('2d');
    }
    if (fontname !== undefined) {
	getTextHeight.ctx.font = fontsize + ' ' + fontname;
    }
    return getTextHeight.ctx.measureText('M').width;
}
function maxWidth(values,border) {
    var swidth=0;
    var mwidth=0;
    var lenv=values.length;
    for (var ii=0;ii<lenv;ii++) {
	var cwidth = getTextWidth(values[ii])+2*border;
	if (cwidth > mwidth) {
	    mwidth=cwidth;
	}
	swidth=swidth+cwidth;
    }
    return {max:mwidth,sum:swidth};
}
function makePlan(label,colvalues,rowvalues,iwidth,iheight,border) {
    var plan={cell:{rotate:false,step:1,border:border,width:100,height:100,xoffset:0,yoffset:0}, 
	      hdr:{rotate:false,step:1,border:border,width:100,height:100,xoffset:0,yoffset:0},
	      row:{rotate:false,step:1,border:border,width:100,height:100,xoffset:0,yoffset:0},
	      col:{rotate:false,step:1,border:border,width:100,height:100,xoffset:0,yoffset:0}};
    if (iwidth <= 0) { return plan;};
    // text boundaries
    var wh=maxWidth(rowvalues,border);
    var ww=maxWidth(colvalues,border);
    var zwidth =getTextWidth(label) + 2 * border;   //props.theme.spacing.unit;
    // var zheight=getTextHeight(label) + 2 * border;  //props.theme.spacing.unit;
    // table boundaries
    var mheight=getTextHeight() + 2 * border;       //props.theme.spacing.unit;
    var hwidth=Math.max(wh.max,zwidth) + mheight;
    var width=iwidth-hwidth;
    // calculate cell width...
    var mwidth=ww.max;
    //var swidth=ww.sum;
    var lenc=colvalues.length;
    var lenr=rowvalues.length;
    var hh, hw, ch, cw, hx, rot, stp;
    if (mwidth*lenc < width) { // horisontal
	rot=false;
	stp=1
	cw=width/lenc;
	hh=mheight;
	hx=(plan.cell.width-mwidth)/2;
	//console.log("Plan (normal):",JSON.stringify(plan));
    } else if (mheight*lenc < width) { // rotate
	rot=true;
	stp=1
	cw=width/lenc;
	hh=mwidth;
	hx=(plan.cell.width-mheight)/2;
	//console.log("Plan (rot):",JSON.stringify(plan),lenc,plan.cell.width*lenc);
    } else { // rotate and use steps
	rot=true;
	stp=Math.ceil(lenc*mheight/width);
	cw=plan.step*width/lenc;
	hh=mwidth;
	hx=(plan.cell.width-mheight)/2;
	//console.log("Plan (rot+step):",JSON.stringify(plan),lenc,height,width);
    }
    // calculate cell height
    var height=iheight-plan.hdr.height;
    if (mheight*lenr < height) { // 
	ch=Math.min(mheight*10,height/lenr);
    } else {
	ch=mheight;
    }
    hw=hwidth;
    setPlan(plan.cell,{width:cw,height:ch});
    setPlan(plan.hdr, {width:hw,height:hh});
    setPlan(plan.col, {width:cw,height:hh,xoffset:hx,step:stp,rotate:rot});
    setPlan(plan.row, {width:hw,height:ch});    
    //console.log("Plan (finally):",JSON.stringify(plan),mheight,mwidth,height,width,lenr);
    return plan;
}
// ---------------- DATA
function DataCell(props) {
    const {classes,state,elements,mode,plan,...other}=props;
    if (elements===undefined) {
	return <div className={classes.divTableCell} style={{backgroundColor:'#EEE'}}/>
    } else if (mode===state.Show.code.cell.Sum) {
	return <SummaryCell {...other} state={state} elements={elements} plan={plan}/>
	//return null;
    } else {
	return <SeriesCell {...other} state={state} elements={elements} plan={plan}/>
	//return null;
    }
}
function renderDataCell(classes,state,colkey,colvalues,colvalue,index,range,mode,plan) {
    var title=state.Database.getWhereDetail(colkey,colvalues[index]);
    //console.log("Making data cell:",colvalue,index,JSON.stringify(plan));
    if ((index-1)%plan.step === 0) {
	// get elements and range
	//console.log("Processing:",colvalues[index],plan.step);
	var elements=state.Matrix.getMatrixElements(colvalues,"",state.React.matrix,index,plan.step);
	//console.log("Elements:",JSON.stringify(elements),mode);
	// get count and colwhere
        var cnt = Math.min(colvalues.length,index+plan.step)-index;
        var colwhere = state.Database.getColWhere(colkey,colvalues,index,plan.step);
	return (<DataCell classes={classes} state={state} key={`col-${index}`} index={index} onclick={onclick} title={title} 
		colkey={colkey} colvalues={colvalues} colval={colvalue} colwhere={colwhere}
		elements={elements} mode={mode} plan={plan} range={range}/>);
    } else {
	return null;
    }
}
function DataRow(props) {
    const {classes,state,colkey,colvalues,mode,plan} = props;
    var range=[undefined,undefined];
    if (state.React.matrix!==undefined) {
	range=state.Matrix.getRange(state,state.React.matrix,colvalues,[""]);
    };

    console.log("Matrix:",JSON.stringify(state.React.matrix));

    console.log("Making data cols.",colkey,JSON.stringify(colvalues));
    console.log("We have a matrix with range:",JSON.stringify(range));
    var mapFunction= (colvalue,index)=>renderDataCell(classes,state,colkey,colvalues,colvalue,index,range,mode,plan);
    return (<div className={classes.divTableRow} key={"1"}>
	       {colvalues.map(mapFunction)}
	    </div>);
};

// ---------------- HDR
function renderHdrCell(classes,state,colkey,colvalues,plan,val,index) {
    if ((index-1)%plan.step === 0) {
	//console.log("HdrCell:",index,plan.step);
        var cnt = Math.min(colvalues.length,index+plan.step)-index;
        var colwhere = state.Database.getColWhere(colkey,colvalues,index,plan.step);
	var onclick=() => state.Navigate.selectKey(state,colkey,colvalues[index],colwhere,cnt);
	var title=colwhere;
	var cursor=classes.divTableCell;
	if (onclick !== undefined) {
	    cursor=classes.divTableCellCursor;
	}
	return (<div className={cursor} onClick={onclick} style={{backgroundColor:'#DDD'}} title={title} key={`col-${index}`} >
		   <CanvasText state={state} index={index} plan={plan.col} label={val}></CanvasText>
 	        </div> );
     } else {
	return null;
    }
}
function HdrRow(props) {
    const { classes, state, plan, colkey, colvalues, label } = props; //, rowvalues
    //console.log("Making header row.",colkey,JSON.stringify(colvalues));
    var mapFunction= (val,index)=>renderHdrCell(classes,state,colkey,colvalues,plan,val,index);
    return (<div className={classes.divTableRow} key={0}>
	       {colvalues.map(mapFunction)}
	    </div>);
}
