		defHttp.addEventListener("error",state.Html.errorInfo);
		defHttp.addEventListener("abort",state.Html.abortInfo);
		defHttp.onreadystatechange = function() {
		    state.Database.ready=true;
		    if (defHttp.readyState  === 4) {
			if (defHttp.status  === 200) {
			    console.log(defHttp.responseText);
			    var e=state.Default.isEmpty;
			    var json = JSON.parse(defHttp.responseText);
			    if (e(state,state.Default.other)&&!e(state,json.other)) {state.Default.other=json.other;}
			    if (e(state,state.Default.trash)&&!e(state,json.trash)) {state.Default.trash=json.trash;}
			    state.Default.Threshold={thrs:json.thrs};
			    state.Default.Colors={colors:json.colors};
			    state.Default.Path={order:json.order,select:json.select,home:json.home};
			    state.Default.Show={state:json.state};
			    state.Default.Layout={priority:json.priority,cfont:json.cfont};
			    state.Default.pushDefault(state)
			    var callback=callbacks.shift();
			    if (callback !== undefined) {
				setTimeout(callback(state,callbacks),0.1);
			    }
			} else if (state.React !== undefined && state.React.Html !== undefined) {
			    state.React.Html.setFootnote(state,"Unable to load "+this.deffile);
			    state.Html.setProgress(state, false);
			}
		    }
		}
		defHttp.responseType="";
		defHttp.overrideMimeType("text/text");
		defHttp.open("GET", "def/"+this.deffile, true);
		defHttp.setRequestHeader('cache-control', 'no-cache, must-revalidate, post-check=0, pre-check=0');
		defHttp.setRequestHeader('cache-control', 'max-age=0');
		defHttp.setRequestHeader('expires', '0');
		defHttp.setRequestHeader('expires', 'Tue, 01 Jan 1980 1:00:00 GMT');
		defHttp.setRequestHeader('pragma', 'no-cache');
		defHttp.send(); 
	    } else {
		state.Html.setFootnote(state,"Already waiting for reply...");
		state.Html.setProgress(state, false);
	    };
	};
    };
    this.pushDefault=function(state) {
	var e=state.Default.isEmpty;
	if (e(state,state.Threshold.thrs)&&!e(state,state.Default.Threshold.thrs)) {state.Threshold.thrs=state.Default.Threshold.thrs;}
	if (e(state,state.Colors.colors)&&!e(state,state.Default.Colors.colors)) {state.Colors.colors=state.Default.Colors.colors;}
	if (e(state,state.Path.order)&&!e(state,state.Default.Path.order)) {state.Path.order=state.Default.Path.order;}
	if (e(state,state.Path.home)&&!e(state,state.Default.Path.home)) {state.Path.home=state.Default.Path.home;}
	if (e(state,state.Path.select)&&!e(state,state.Default.Path.select)) {state.Path.select=state.Default.Path.select;}
	if (e(state,state.Show.state)&&!e(state,state.Default.Show.state)) {state.Show.state=state.Default.Show.state;}
	if (e(state,state.Layout.priority)&&!e(state,state.Default.Layout.priority)) {state.Layout.priority=state.Default.Layout.priority;}
	if (e(state,state.Layout.cfont)&&!e(state,state.Default.Layout.cfont)) {state.Layout.cfont=state.Default.Layout.cfont;}
	// console.log("Default thrs:",JSON.stringify(state.Threshold.thrs));
