	//console.log("Default path:",JSON.stringify(state.Path.home));
	//console.log("Done loading:",JSON.stringify(state.Default.trash));
    };
    this.hasChanged=function(state,main,item) {
	if (state.Default!==undefined && 
	    state.Default[main]!==undefined &&
	    state.Default[main][item]!==undefined) {
	    var src=JSON.stringify(state[main][item]);
	    var trg=JSON.stringify(state.Default[main][item]);
	    //console.log("Checking:",main,item,"\n",src,"\n",trg);
	    return src!==trg;
	} else {
	    return true;
	}
    };
