import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import TableCell from '@material-ui/core/TableCell';


const styles = theme => ({
    pointer: {
	cursor:"pointer",
	border:"0px",
	overflow: 'hidden',
	padding: theme.spacing.unit*0,
	textAlign:"center",
    },
    nopointer: {
	border:"0px",
	padding: theme.spacing.unit*0,
	textAlign:"center",
    },
    divTableCell:{
	border: '0px solid #999999',
	display: 'table-cell',
	padding: '0px 0px',
    },
    divTableCellCursor:{
	cursor: "pointer",
	border: '0px solid #999999',
	display: 'table-cell',
	padding: '0px 0px',
    },
});

//	borderCollapse: 'collapse',

function SummaryCell(props) {
    const { state,classes,onclick,
	    colwhere,rowwhere,label,
	    elements, mode, rowindex } = props;
    if (elements===undefined) {
	if (rowindex%2===1) {
	    return <div className={classes.divTableCell} style={{backgroundColor:'#EEE'}}/>
	} else {
	    return <div className={classes.divTableCell} style={{backgroundColor:'#FFF'}}/>
	}
    } 
    var cnt=0;
    var lev=-1;
    var elen=elements.length;
    for (var ee=0;ee<elen;ee++) {
        cnt=cnt+elements[ee].cnt;
        lev=Math.max(lev,elements[ee].lev);
    }; 
    var lab="";
    if (label === undefined) {
	if (cnt > 1) {lab=cnt;};
    } else {
	lab=label;
    };
    var bgcolor=state.Colors.getLevelColor(lev);
    var cursor=classes.divTableCell;
    if (onclick !== undefined) {
	cursor=classes.divTableCellCursor;
    }
    var title="";
    if (colwhere !== "" && rowwhere !== "") {
	title="("+colwhere+") AND ("+rowwhere+")";
    } else if (colwhere !== "") {
	title=colwhere;
    } else if (rowwhere !== "") {
	title=rowwhere;
    }
    //{bgcolor}
    //console.log("SummaryCell:",lab," lev=",lev,elements.length,bgcolor);
    return (
            <div className={cursor} style={{backgroundColor:bgcolor}} onClick={onclick} title={title}>
	    {lab}
	</div>
    );
}

SummaryCell.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(SummaryCell);
