//console.log("Loading HtmlLib.js");

function Html() {
    this.ceye="&#x1f441";
    this.setLog=function(state,msg) {
	if (state.React !== undefined && state.React.Status !== undefined) {
	    if (msg  === undefined) {msg=state.Utils.getStatusString(state);};
	    //console.log("setlog:",msg,state.Matrix.cnt);
	    state.React.Status.setLog(state,msg);
	}
    };
    this.getTableStyle=function(bcolor) {
	return "padding:5pt;min-width:25px;width:25px;background-color:"+bcolor+";";
    };
    this.getButtonStyle=function(bcolor,fcolor,border) {
	//console.log("ButtonStyle:",bcolor,fcolor,border);
	if (border) {
	    return "font-size:15px;background-color:"+bcolor+";color:"+fcolor+
		";padding:5px 5px;border-radius:10px;cursor:pointer;border-color:black;"
	} else {
	    return "font-size:15px;background-color:"+bcolor+";color:"+fcolor+
		";padding:5px 5px;border-radius:10px;;cursor:pointer;border:none;"
	}
    };
    this.progressInfo=function(state,e) {
	if (state.React !== undefined && state.React.Status !== undefined) {
	    if (e.loaded  === e.total) {
		state.React.Status.setLog(state,"");
	    } else {
		e.preventDefault();
		console.log("Complete:",e.loaded,e.total,JSON.stringify(e));
		//var percentComplete = e.loaded / e.total * 100;
		//state.React.Status.setLog(state,percentComplete.toFixed(2)+"kb");
		state.React.Status.setLog(state,e.loaded+" kb");
	    }
	}
    };
