    this.makeDataElement=function(state,elements,onclick,title,step) {
	var label="";
	var cnt=0;
	var lev=-1;
	var elen=elements.length;
	for (var ee=0;ee<elen;ee++) {
	    cnt=cnt+elements[ee].cnt;
	    lev=Math.max(lev,elements[ee].lev);
	}; 
	if (cnt > 1) {label=cnt;};
	var bcolor=state.Colors.getLevelColor(state,lev);
	var td=document.createElement("TD");
	td.setAttribute("align","center");
	td.setAttribute("ondrop","Navigate.drop(state,event)");
	td.setAttribute("ondragover","Navigate.dropCheck(state,event)");
	td.setAttribute("draggable","true");
	td.setAttribute("ondragstart","Navigate.dragStart(state,event)");
	td.setAttribute("ondragend","Navigate.dragEnd(state,event)");
	td.setAttribute("ptyp","item");
	td.setAttribute("pval",lev);
	td.setAttribute("class","full");
	if (title !== "") {
	    td.setAttribute("title",title);
	}
	var txt=document.createElement("TEXT");
	txt.setAttribute("ptyp","item");
	var style="background-color:"+bcolor+";color:"+state.Colors.colors.fg+";white-space: nowrap;";
	if (onclick !== undefined && onclick !== "") {
	    td.setAttribute("onclick",onclick);
	    style=style+"cursor:pointer;";
	}
	td.setAttribute("style",style+"padding:5pt;min-width:25px;width:25px;");
	txt.setAttribute("style",style+"width:100%;height:100%;");
	txt.setAttribute("class","full");
	if (label !== undefined && label !== null) {
	    txt.innerHTML=label;
	} else {
	    txt.innerHTML="";
	}
	if (title !== "") {
	    txt.setAttribute("title",title);
	}
	td.appendChild(txt);
	return td;
    }

    // canvas element

    this.makeCanvasElement=function(state,elements,key,val,range,onclick,title,step) { 
	var td=document.createElement("TD");
	//td.setAttribute("style","text-decoration: none;padding-bottom:0pt");
	td.setAttribute("pval",val[0]);
	td.setAttribute("align","center");
	td.setAttribute("ptyp","canvas");
	td.setAttribute("class","canvas");
	var cnv=document.createElement("CANVAS");
	cnv.setAttribute("style","width:100%;height:100%;display:block;cursor:pointer;");
	//var width = cnv.width;
	var height = cnv.height;
	var ctx = cnv.getContext(state,"2d");
	var elen=elements.length;
	var ee;
	var tot=0; for (ee=0;ee<elen;ee++) {tot=tot+elements[ee].docs.length;};
	var first=true;
	var cnt=0;
	var dw= cnv.width/Math.max(step,tot)
	//console.log("******** Canvas elements:",elen,tot,dw,width)
	for (ee=0;ee<elen;ee++) {
	    var el=elements[ee];
	    var level=el.lev;
	    var color=state.Colors.getLevelColor(state,level);
	    var docs=el.docs;
	    var dlen=docs.length;
	    //console.log("Element:",level,color,tot,cnt);
	    if (dlen>0) {
		for (var jj=0;jj<dlen;jj++) {
		    cnt=cnt+1;
		    var d=docs[jj];
		    state.Threshold.setGThr(state,d);
		    var lev=state.Threshold.getLevel(state,d);
		    var col=state.Colors.getLevelColor(state,lev);
		    //console.log("   Doc:",key,jj,JSON.stringify(lev),JSON.stringify(d));
		    for (var ii=0;ii<step;ii++) {
			//console.log("   Checking Val:",key,ii,val[ii],d[key]);
			//console.log("Position:",ii,jj,dlen,step,key,d[key],val[ii])
			if (d[key]  === val[ii]) {
			    if (first) {
				first=false;
				//console.log("Doc:",jj,JSON.stringify(d));
			    };
			    var t=state.Threshold.getThresholds(d);
			    //console.log("Making canvas:",ii,val[ii],color,JSON.stringify(d),
			    //	    " Thr=",JSON.stringify(t),width,height,JSON.stringify(range));
			    //console.log("Canvas:",ii,jj,d.dtg,color,level,JSON.stringify(t));
			    var min=range[0]
			    var max=range[1];
			    var ymin=min;
			    if (t.min !== undefined && t.min[state.Threshold.ival] !== undefined) {
				ymin=t.min[state.Threshold.ival];
			    }
			    var ymax=max;
			    if (t.max !== undefined && t.max[state.Threshold.ival] !== undefined) {
				ymax=t.max[state.Threshold.ival];
			    }
			    var mm=ii
			    if (step<tot) {mm=cnt-1;}
			    //console.log(" canvas position:",mm,ii,cnt,step,tot)
			    var xmin=mm*dw;       // width/10;
			    var xmax=(mm+1)*dw-2;   //width-2*xmin;
			    var zmin=state.Show.scale(ymin,min,max,height,0);
			    var zmax=state.Show.scale(ymax,min,max,height,0);
			    //console.log("Fill:",xmin,xmax,width,zmin,(zmax-zmin),height,color);
			    //ctx.fillStyle="cornflowerblue";
			    color=col;
			    if (color !== undefined) {ctx.fillStyle=color;}
			    ctx.fillRect(xmin,zmin,xmax-xmin,(zmax-zmin));
			    //ctx.beginPath();
			    //ctx.lineWidth=2;
			    //if (color !== undefined) {ctx.strokeStyle=color;}
			    //ctx.strokeStyle="black";
			    //ctx.moveTo(xmin,zmax);
			    //ctx.lineTo(xmax,zmax);
			    //ctx.stroke();
			    // draw thresholds
			    for (var msr in t) {
				var tt    = t[msr];
				var tmax  = tt[state.Threshold.imax];
				var tthr  = tt[state.Threshold.ithr];
				var tkey  = tt[state.Threshold.ikey];
				//var tlev  = tt[state.Threshold.ilev];
				//var tval  = tt[state.Threshold.ival];
				var ts;
				if (tmax) {
				    ts=state.Threshold.thrs[tthr][tkey].max;
				} else {
				    ts=state.Threshold.thrs[tthr][tkey].min;
				};
				var tlen=ts.length;
				for (var ll=0;ll<tlen;ll++) {
				    var tyval=ts[ll];
				    var tzval=state.Show.scale(tyval,min,max,height,0);
				    var scolor=state.Colors.getLevelColor(state,ll);
				    ctx.beginPath();
				    ctx.lineWidth=5;
				    if (scolor !== undefined) {ctx.strokeStyle=scolor;}
				    ctx.moveTo(xmin,tzval);
				    ctx.lineTo(xmax,tzval);
				    ctx.stroke();
				    //console.log("Stroke color:",color,ll,tzval,width);
				}
			    }
			}
		    }
		}
	    }
	};
	if (tot  === 0) {
	    console.log("No draw:",JSON.stringify(val),JSON.stringify(docs),dlen);
	}
	if (title !== "") {
	    td.setAttribute("title",title);
	}
	if (onclick !== undefined && onclick !== "") {
	    td.setAttribute("onclick",onclick);
	}
	td.appendChild(cnv);
	return td;
    };
