    this.rotateHeight=function(state) {
	var gwidth;
	var gdd;
	var rotates = document.getElementsByClassName('rotate');
	//console.log("Rotating heights.",rotates.length);
	var i;
	for (i = 0; i < rotates.length; i++) {
	    var lheight=parseInt(state,rotates[i].offsetWidth,10);
	    var lwidth=parseInt(state,rotates[i].offsetHeight,10);
	    if (gwidth  === undefined) {gwidth=lwidth;};
	    var oheight=(lheight/2);
	    var owidth=(lwidth/2);
	    var dd=(oheight-owidth);
	    if (gdd  === undefined) {
		gdd=dd;
	    } else if (gdd < dd) {
		gdd=dd;
	    }
	}
	for (i = 0; i < rotates.length; i++) {
	    var cwidth=parseInt(state,window.getComputedStyle(rotates[i]).fontSize,10);
	    var lheight=parseInt(state,rotates[i].offsetWidth,10);
	    var lwidth=parseInt(state,rotates[i].offsetHeight,10);
	    var oheight=(lheight/2);
	    var owidth=(lwidth/2);
	    var dd=(oheight-owidth);
	    var style="width:"+lwidth+"px;height:"+lheight+"px;transform:translate(state,"+dd+"px,"+gdd+"px) rotate(state,-90.0deg);";
	    //console.log("Rotates:",i,style,lwidth,lheight,gwidth,cwidth,oheight,owidth);// 19px 129px
	    rotates[i].setAttribute("style",style);
	}
	var canvas = document.getElementsByClassName('canvas');
	for (i = 0; i < canvas.length; i++) {
	    var cheight=parseInt(state,window.getComputedStyle(canvas[i]).fontSize,10);
	    var style="width:"+gwidth+"px;height:"+cheight*1.5+"px";
	    //console.log("Canvas:",i,style,gwidth);// 19px 129px
	    canvas[i].style.width=gwidth+"px";
	    canvas[i].style.height=(cheight*3)+"px";
	}
    };
    this.setAttributes=function(item,hash) {
	for (var name in hash) {
	    var val=hash[name];
	    item.setAttribute(name,val);
	}
    }
