//console.log("Loading ThresholdLib.js");

function Threshold() {
    this.thr=undefined; // threshold parameter levels
    this.gthr=undefined;// temporary storage
    this.imax=0;        // threshold item types
    this.ithr=1;
    this.ikey=2;
    this.ilev=3;
    this.ival=4;
    this.init=function(state){
	var par="Threshold";
	state.Utils.init(par,this);
    };
    this.getThresholds=function(doc,thrs) {
	if (doc.thresholds !== undefined) {
	    return doc.thresholds;
	} else {
	    if (thrs === undefined) {
		thrs=this.thrs;
	    }
	    var subthr={};
	    for (var thr in thrs) { // loop over thresholds
		if (doc[thr] !== undefined) {
		    var key=doc[thr]; // key-value
		    if (thrs[thr][key] !== undefined) {
			var tlen,dlev,jj,res;
			if (thrs[thr][key].latlon === undefined) {
			    return this.getThresholds(doc,thrs[thr][key]);
			} else if (thrs[thr][key].max !== undefined && doc.max !== undefined) {
			    var dmax=Number(doc.max);
			    dlev=-1;
			    var tmax=thrs[thr][key].max;
			    tlen=tmax.length;
			    // get new level
			    for (jj = 0; jj < tlen; jj++) {
				if (dmax >= Number(tmax[jj])) {
				    //console.log("Hit:",jj,dmax,tmax[jj],dmax>=tmax[jj],dlev);
				    dlev=jj;
				}
			    }
			    res=[];
			    res[this.imax]=true;
			    res[this.ithr]=thr;
			    res[this.ikey]=key;
			    res[this.ilev]=dlev;
			    res[this.ival]=dmax;
			    subthr.max=res;
			} else if (thrs[thr][key].min !== undefined && doc.min !== undefined) { // min value
			    dlev=-1;
			    var tmin=thrs[thr][key].min;
			    var dmin=Number(doc.min);
			    // get new level
			    tlen=tmin.length;
			    for (jj = 0; jj < tlen; jj++) {
				if (dmin <= Number(tmin[jj])) {
				    dlev=jj;
				}
			    }
			    res=[];
			    res[this.imax]=false;
			    res[this.ithr]=thr;
			    res[this.ikey]=key;
			    res[this.ilev]=dlev;
			    res[this.ival]=dmin;
			    subthr.min=res;
			}
			//if (dlev !== -1) {
			//    console.log("Found level:",dlev,dmax,
			//    		JSON.stringify(tmax),thr,key);
			//} else {
			//    console.log("Mssing level:",dlev,dmax,
			//    		JSON.stringify(tmax),thr,key);
			//}
		    }
		}
	    }
	    doc.thresholds=subthr;
	    return subthr;
	}
    };
    this.setGThr=function(state,doc) {
	if (this.gthr  === undefined ||
	    doc[this.gthr[0]]  === undefined ||
	    doc[this.gthr[0]] !== this.gthr[1]) {
	    this.gthr=this.getThreshold(state,doc);
	};
    }

    // call after this.setGThr
    this.getLevel=function(state,doc) {
	var lev=-1;
	if (this.gthr  === undefined) {
	    lev=-2;
	} else if (this.gthr[2].max !== undefined) {
	    lev=this.getThrLevel(state,doc,this.gthr[2],"max");
	} else if (this.gthr[2].min !== undefined) {
	    lev=this.getThrLevel(state,doc,this.gthr[2],"min");
	};
	return lev;
    }

    // call after this.setGThr
    this.getKeyLat=function(state) {
	return this.gthr[2]["latlon"][0];
    }

    // call after this.setGThr
    this.getKeyLon=function(state) {
	return this.gthr[2]["latlon"][1];
    }

    // call after this.setGThr
    this.getLat=function(state,doc) {
	if (this.gthr  === undefined) {
	    return undefined;
	} else if (this.gthr[2]["latlon"] !== undefined) {
	    return doc[this.getKeyLat(state)];
	};
    }

    // call after this.setGThr
    this.getLon=function(state,doc) {
	if (this.gthr  === undefined) {
	    return undefined;
	} else if (this.gthr[2]["latlon"] !== undefined) {
	    return doc[this.getKeyLon(state)];
	};
    };
    this.getThreshold=function(state,doc) {
	if (doc.threshold !== undefined) {
	    return doc.threshold;
	} else {
	    for (var thr in this.thrs) { // loop over thresholds
		if (doc[thr] !== undefined) {
		    var key=doc[thr]; // key-value
		    if (this.thrs[thr][key] !== undefined) {
			var threshold= [thr,key,this.thrs[thr][key]];
			doc.threshold=threshold;
			return threshold;
		    }
		}
	    }
	    return;
	}
    };
    this.getThrLevel=function(state,doc,thrs,key) {
	var tlen,jj;
	var lev=-1;
	var val=doc[key];
	var thr=thrs[key];
	if (key  === "max") {
	    tlen=thr.length;
	    for (jj = 0; jj < tlen; jj++) {
		if (val >= Number(thr[jj])) {
		    //console.log("Hit:",jj,val,thr[jj]);
		    lev=jj;
		}
	    }
	} else if (key  === "min") {
	    tlen=thr.length;
	    for (jj = 0; jj < tlen; jj++) {
		if (val <= Number(thr[jj])) {
		    //console.log("Hit:",jj,val,thr[jj]);
		    lev=jj;
		}
	    }
	}
	return lev;
    }
};
export default Threshold;
