//console.log("Loading DatabaseLib.js");

const alasql = window.alasql;

function Database() {
    this.cnt=0;
    this.keyCnt={};
    this.values= {};
    this.epoch0=0;
    this.regfile="register";      // register file (shows current datfile)
    this.datfile="";              // json data file
    this.arrayConstructor=[].constructor;
    this.objectConstructor={}.constructor;
    this.casc=0; // key is sorted ascending
    this.cdes=1; // key is sorted descending
    this.nasc=2; // key is sorted ascending
    this.ndes=3; // key is sorted descending
    this.delay=100000;  // server-polling period in ms
    this.ready=true;     // can we poll server or is another poll running
    this.log="";
    this.mod="";
    this.init=function(state){
	var par="Database"+state.Utils.version;
	state.Utils.init(par,this);
    };
    this.update=function(state) {
	this.setTime(state);
	this.load(state);
	setTimeout(()=>function() {this.update(state)},this.delay);
    };
    this.load=function(state) {
	//console.log("Loading...",this.cnt++);
	state.Default.loadDefault(state,[this.loadRegister,
					 this.loadData,
					 //function(state,callbacks){console.log(">>> Default:",JSON.stringify(state.Default.trash));}
					]);
    };
    this.loadRegister=function(state, callbacks ) {
	//console.log("loadRegister");
	if (this.ready) { // make sure we do not re-load if we are already loading
	    this.ready=false;
	    state.Html.setLog(state, "Server-request: "+this.regfile);
	    var regHttp = new XMLHttpRequest();
	    regHttp.addEventListener("progress",(e)=>state.Html.progressInfo(state,e));
	    //regHttp.addEventListener("load",(e)=>state.Html.loadInfo(state,e));
	    regHttp.addEventListener("error",(e)=>state.Html.errorInfo(state,e));
