		    if (regHttp.status  === 200) {
			//console.log(regHttp.responseText);
			var file = regHttp.responseText;
			if (file !== this.datfile) { // load new data
	    		    this.datfile=file;
			    var callback=callbacks.shift();
			    if (callback !== undefined) {
				setTimeout(callback(state,file,callbacks),0);
			    }
			    state.Html.setInfo(file);
			} else {
			    state.Html.setInfo(file);
			    state.Html.setLog(state);
			}
		    } else {
			state.Html.setLog(state,"Unable to load "+this.regfile);
			state.Html.setInfo(this.regfile+" error");
		    }
		} else {
		    state.Html.setInfo(this.regfile+" error");		
		};
	    }.bind(this);
	    regHttp.responseType="";
	    regHttp.overrideMimeType("text/text");
	    regHttp.open("GET", "data/"+this.regfile, true);
	    regHttp.setRequestHeader('cache-control', 'no-cache, must-revalidate, post-check=0, pre-check=0');
	    regHttp.setRequestHeader('cache-control', 'max-age=0');
	    regHttp.setRequestHeader('expires', '0');
	    regHttp.setRequestHeader('expires', 'Tue, 01 Jan 1980 1:00:00 GMT');
	    regHttp.setRequestHeader('pragma', 'no-cache');
	    regHttp.send(); 
	} else {
	    state.Html.setInfo("");
	    state.Html.setLog(state,"Already waiting for reply...");
	};
    }.bind(this);
    this.loadData=function(state,file, callbacks ) {
	//console.log("loadData");
	if (this.ready) { // make sure we do not re-load if we are already loading
	    this.ready=false;
	    state.Html.setLog(state, "Server-request: "+this.datfile);
	    var datHttp = new XMLHttpRequest();
	    datHttp.addEventListener("progress",(e)=>state.Html.progressInfo(state,e));
	    //datHttp.addEventListener("load",(e)=>state.Html.loadInfo(state,e));
	    datHttp.addEventListener("error",(e)=>state.Html.errorInfo(state,e));
	    datHttp.addEventListener("abort",(e)=>state.Html.abortInfo(state,e));
	    datHttp.onreadystatechange = function() {
		this.ready=true;
		if (datHttp.readyState  === 4) {
		    if (datHttp.status  === 200) {
			console.log("Loaded "+this.datfile);
			state.Html.setInfo("100%");
			state.Html.broadcast(state,"Loaded "+this.datfile);
	    		state.Html.setLog(state,"Creating database");
			console.log("Creating database from "+file);
			setTimeout(function() {
			    this.dbInsert(state,datHttp.responseText);
