//console.log("Loading ThresholdLib.js");

function Threshold() {
    this.thrs=undefined; // threshold parameter levels, set by Default
    this.imax=0;        // threshold item types
    this.ithr=1;
    this.ikey=2;
    this.ilev=3;
    this.ival=4;
    this.init=function(state){
	var par="Threshold";
	state.Utils.init(par,this);
    };
    this.setThresholds=function(doc,thrs) {
	if (doc.thr !== undefined) {
	    return [];
	} else {
	    if (thrs === undefined) {
		thrs=this.thrs;
	    };
	    for (var trgkey in thrs) { // loop over thresholds
		if (doc[trgkey] !== undefined) {
		    var trgval=doc[trgkey]; // trgval-value
		    if (thrs[trgkey][trgval] !== undefined) {
			var mlen,jj,res;
			if (thrs[trgkey][trgval].par === undefined) { // there is another level
			    return this.getThresholds(doc,thrs[trgkey][trgval]);
			} else if (doc[thrs[trgkey][trgval].par] !== undefined) {
			    var thr = thrs[trgkey][trgval]
			    doc.threshold=thr;
			    doc.thr={};
			    var val = doc[thr["par"]];
			    var maxs = thr[">"];
			    var mins = thr["<"];
			    var doclev=-1;
			    if (max !== undefined) { // above thresholds
				var docmax=Number(val);
				mlen=maxs.length;
				// get new level
				for (jj = 0; jj < mlen; jj++) {
				    if (docmax >= Number(maxs[jj])) {
					//console.log("Hit:",jj,docmax,tmax[jj],docmax>=tmax[jj],doclev);
					doclev=jj;
				    }
				}
				doc.thr.level=doclev;
				doc.thr.val=docmax
				doc.thr.lat=doc[thrs["lat"]];
				doc.thr.lon=doc[thrs["lon"]];
				doc.thr.max=max;
				return ["level","val","lat","lon","max"];
			    } else if (min !== undefined) { // below thresholds
				var docmin=Number(val);
				// get new level
				mlen=mins.length;
				for (jj = 0; jj < tlen; jj++) {
				    if (docmin <= Number(mins[jj])) {
					doclev=jj;
				    }
				}
				doc.thr.level=doclev;
				doc.thr.val=docmin
				doc.thr.lat=doc[thrs["lat"]];
				doc.thr.lon=doc[thrs["lon"]];
				doc.thr.min=min;
				return ["level","val","lat","lon","min"];
			    }
			}
		    }
		}
	    }
	    doc.level=-2;  // no thresholds found...
	}
	return [];
    };
    // call after this.setThresholds
    this.getLevel=function(state,doc) {
	return doc.level;
    }
    this.getLat=function(state,doc) {
	return doc.lat;
    }
    this.getLon=function(state,doc) {
	return doc.lon;
    };
};
export default Threshold;
