import React, {Component} from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Popper from '@material-ui/core/Popper';

//import { CompactPicker as Picker } from 'react-color';
import { ChromePicker as Picker } from 'react-color';

const styles = theme => ({
    settings: {
        marginRight: 'auto',
	color:'red',
    },
    button:{color:'white'},
    paper: {
	border: '1px solid',
	padding: '10px',
	backgroundColor: theme.palette.background.paper,
    },
    divTableCell:{
	border: '0px solid #999999',
	display: 'table-cell',
	padding: '0px',
//	border:  '1px solid blue',
    },
});

class LevelColor extends Component {
    state = {anchor: null};
    constructor() {
	super();
	this.open=this.open.bind(this);
	this.close=this.close.bind(this);
	this.myRef=React.createRef();
    }
    open(event) {
	// ... show the menu element ...
	this.setState({ anchor: event.currentTarget })
	const clickOff = (evt) => { 
	    var node=this.myRef.current;
	    if (node.contains(evt.target)) {
		console.log("Inside doc...");
	    } else {
		document.removeEventListener('click', clickOff.bind(this));
		this.close();
	    }
	};
	document.addEventListener('click', clickOff.bind(this));
    };
    close() {
	// ... hide the menu element ...
	this.setState({ anchor: null });
    };
    render() {
        const { state,classes,bg,fg,level } = this.props;
	console.log("Rendering LevelColor...");
	this.onClose = () => {this.close();};
	this.onClick = (event) => {this.open(event)};
	const handleColorChange = ({ hex }) => {console.log("Color:",hex,level);state.Colors.setLevelBgColor(level,hex);state.Show.show(state,false);}
	var style={color:fg,backgroundColor:bg,textAlign:'center'};
	return (<div className={classes.divTableCell} style={style}>
		  <div
                    aria-owns={this.state.anchor ? 'popper' : undefined}
                    aria-haspopup="true"
                    onClick={this.onClick}
		   >
		      {level}
		  </div>
	          <Popper
		   className={classes.paper} 
		   settings={{float:'right'}}
                   id="popper"
	           anchorEl={this.state.anchor}
                   open={Boolean(this.state.anchor)}
	          >
		    <Picker className={classes.paper} color={bg} onChangeComplete={ handleColorChange }/>
	          </Popper>
		</div>
	       );
    }
}

LevelColor.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(LevelColor);
