import React, { Component} from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';

const styles = theme => ({
    canvas: {
    },
    pointer: {
	cursor:"pointer"
    },
    nopointer: {
    },
});

function updateCanvas(item) {
    const {state,elements,colkey,rowkey,colvalues,index,range,plan,level} = item.props;
    const cnv=item.refs.canvas;        // canvas
    const ctx = cnv.getContext('2d');  // context
    const step=plan.step;
    var height = cnv.height;
    var elen=elements.length;
    var blev=-1;
    var ee;
    var tot=0; for (ee=0;ee<elen;ee++) {tot=tot+elements[ee].docs.length;}; // total number of segments
    var cnt=0;
    var dw= cnv.width/Math.max(step,tot); // width of graph segment
    var clen=0; if (colvalues !== undefined) {clen=colvalues.length;}; // number of columns
    //console.log("******** Canvas elements:",elen,tot,dw,width)
    console.log("Range:",JSON.stringify(range));
    var mlevel=-1;
    var mtt=undefined;
    // draw thresholds
    for (ee=0;ee<elen;ee++) {   // loop over elements
	var el=elements[ee];
	var elevel=el.lev;
	var color=state.Colors.getLevelColor(elevel);
	blev=Math.max(blev,elevel);
	var docs=el.docs;
	var dlen=docs.length;
	//console.log("   El:",colkey,ee,elevel,blev); // JSON.stringify(lev),JSON.stringify(d),
	//console.log("Element:",elevel,color,tot,cnt);
	if (dlen>0) {
	    for (var jj=0;jj<dlen;jj++) {   // loop over segments in each element
		cnt=cnt+1;
		var d=docs[jj];
		state.Threshold.setGThr(state,d);
		//var lev=state.Threshold.getLevel(state,d);
		//var col=state.Colors.getLevelColor(lev);
		//console.log("   Doc:",colkey,jj,d.level,col,JSON.stringify(lev),JSON.stringify(d)); // 
		for (var ii=index;ii<Math.min(index+step,clen);ii++) {
		    //console.log("   Checking Val:",colkey,rowkey,ii,colvalues[ii],d[colkey]);
		    //console.log("Position:",ii,jj,dlen,step,colkey,rowkey,colvalues[ii],d[colkey])
		    if (d[colkey]  === colvalues[ii]) {
			var t=state.Threshold.getThresholds(d);
			//console.log("Making canvas:",ii,colvalues[ii],color,JSON.stringify(d),
			//	    " Thr=",JSON.stringify(t),width,height,JSON.stringify(range));
			//console.log("CanvasGraph:",ii,jj,d.dtg,color,elevel,JSON.stringify(t));
			var min=range[0]
			var max=range[1];
			var mm=ii-index
			if (step<tot) {mm=cnt-1;}
			//console.log(" canvas position:",mm,ii,cnt,step,tot)
			var xmin=mm*dw;       // width/10;
			var xmax=(mm+1)*dw-2;   //width-2*xmin;
			if (color !== undefined) {
			    for (var msr in t) {
				var tt    = t[msr];
				var tmax  = tt[state.Threshold.imax];
				var tthr  = tt[state.Threshold.ithr];
				var tkey  = tt[state.Threshold.ikey];
				//var tlev  = tt[state.Threshold.ilev];
				//var tval  = tt[state.Threshold.ival];
				var ts;
				if (tmax) {
				    ts=state.Threshold.thrs[tthr][tkey].max;
				} else {
				    ts=state.Threshold.thrs[tthr][tkey].min;
				};
				var tlen=ts.length;
				for (var ll=0;ll<tlen;ll++) {
				    var tyval=ts[ll];
				    var tzval=state.Show.scale(tyval,min,max,height,0);
				    var scolor=undefined;
				    if (ll === level) {
					scolor='black';
				    } else {
					scolor=state.Colors.getLevelColor(ll);
				    };
				    ctx.beginPath();
				    ctx.lineWidth=2;
				    if (scolor !== undefined) {ctx.strokeStyle=scolor;}
				    ctx.moveTo(xmin,tzval);
				    ctx.lineTo(xmax,tzval);
				    ctx.stroke();
				    //console.log("Stroke color:",scolor,ll,tzval,cnv.width);
				}
			    }
			}
		    }
		}
	    }
	}
    }
    // draw data
    for (ee=0;ee<elen;ee++) {   // loop over elements
	var el=elements[ee];
	var elevel=el.lev;
	var color=state.Colors.getLevelColor(elevel);
	blev=Math.max(blev,elevel);
	var docs=el.docs;
	var dlen=docs.length;
	//console.log("   El:",colkey,ee,elevel,blev); // JSON.stringify(lev),JSON.stringify(d),
	//console.log("Element:",elevel,color,tot,cnt);
	if (dlen>0) {
	    for (var jj=0;jj<dlen;jj++) {   // loop over segments in each element
		cnt=cnt+1;
		var d=docs[jj];
		state.Threshold.setGThr(state,d);
		var lev=state.Threshold.getLevel(state,d);
		var col=state.Colors.getLevelColor(lev);
		//console.log("   Doc:",colkey,jj,d.level,col,JSON.stringify(lev),JSON.stringify(d)); // 
		for (var ii=index;ii<Math.min(index+step,clen);ii++) {
		    //console.log("   Checking Val:",colkey,rowkey,ii,colvalues[ii],d[colkey]);
		    //console.log("Position:",ii,jj,dlen,step,colkey,rowkey,colvalues[ii],d[colkey])
		    if (d[colkey]  === colvalues[ii]) {
			var t=state.Threshold.getThresholds(d);
			//console.log("Making canvas:",ii,colvalues[ii],color,JSON.stringify(d),
			//	    " Thr=",JSON.stringify(t),width,height,JSON.stringify(range));
			//console.log("CanvasGraph:",ii,jj,d.dtg,color,elevel,JSON.stringify(t));
			var min=range[0]
			var max=range[1];
			var ymin=min;
			if (t.min !== undefined && t.min[state.Threshold.ival] !== undefined) {
			    ymin=t.min[state.Threshold.ival];
			}
			var ymax=max;
			if (t.max !== undefined && t.max[state.Threshold.ival] !== undefined) {
			    ymax=t.max[state.Threshold.ival];
			}
			var mm=ii-index
			if (step<tot) {mm=cnt-1;}
			//console.log(" canvas position:",mm,ii,cnt,step,tot)
			var xmin=mm*dw;       // width/10;
			var xmax=(mm+1)*dw-2;   //width-2*xmin;
			var zmin=state.Show.scale(ymin,min,max,height,0);
			var zmax=state.Show.scale(ymax,min,max,height,0);
			console.log("Fill:",xmin,xmax,width,zmin,(zmax-zmin),height,color);
			//ctx.fillStyle="cornflowerblue";
			color=col;
			if (color !== undefined) {
			    ctx.fillStyle='black';
			    ctx.fillRect(xmin,zmin,xmax-xmin,(zmax-zmin));
			};
		    }
		}
	    }
	}
    };
    if (tot  === 0) {
	//console.log("No draw:",JSON.stringify(colvalues),JSON.stringify(docs),dlen);
    } else {
	//console.log("Drew:",JSON.stringify(colvalues),tot,dlen);
    }
    ctx.lineWidth=5;
    var bcolor=state.Colors.getLevelColor(blev);
    //console.log("   Box:",colkey,blev,bcolor); // JSON.stringify(lev),JSON.stringify(d),
    if (bcolor !== undefined) {
	ctx.strokeStyle=bcolor;
	ctx.strokeRect(0,0, cnv.width,cnv.height);
    }
    //console.log("CanvasGraph:",cnv.width,cnv.height);
}


class CanvasGraphComponent extends React.Component {
    constructor(props) {
        super(props);
        const {state} = props;
    };
    componentDidMount() {
        updateCanvas(this);
    }
    componentDidUpdate() {
        updateCanvas(this);
    }
    render() {
        const { classes, onclick, title, plan, ...other } = this.props;
        return (
		<canvas {...other} className={classes.canvas} classes={classes} onClick={onclick} title={title} plan={plan} ref="canvas" width={plan.width} height={plan.cell}/>
        );
    }
}

CanvasGraphComponent.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(CanvasGraphComponent);
