import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import TableCell from '@material-ui/core/TableCell';


const styles = theme => ({
    pointer: {
	cursor:"pointer",
	border:"1px",
    },
    nopointer: {
	border:"1px",
    },
});
function SummaryCell(props) {
    const { state,classes,onclick,
	    colwhere,rowwhere,
	    elements } = props;
    var cnt=0;
    var lev=-1;
    var elen=elements.length;
    for (var ee=0;ee<elen;ee++) {
        cnt=cnt+elements[ee].cnt;
        lev=Math.max(lev,elements[ee].lev);
    }; 
    var label="";
    if (cnt > 1) {label=cnt;};
    var bgcolor=state.Colors.getLevelColor(lev);
    var cursor=classes.nopointer;
    if (onclick !== undefined) {
	cursor=classes.pointer;
    }
    var title="";
    if (colwhere !== "" && rowwhere !== "") {
	title="("+colwhere+") AND ("+rowwhere+")";
    } else if (colwhere !== "") {
	title=colwhere;
    } else if (rowwhere !== "") {
	title=rowwhere;
    }
    //console.log("SummaryCell:",label," lev=",lev,elements.length,bgcolor);
    return (
         <TableCell className={cursor} bgcolor={bgcolor} onClick={onclick} title={title}>{label}</TableCell>
    );
}

SummaryCell.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(SummaryCell);
