import React, {Component} from "react";
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';

import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';

import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableRow from '@material-ui/core/TableRow';
import TableHead from '@material-ui/core/TableHead';
import TableCell from '@material-ui/core/TableCell';
import ReactDOM from 'react-dom';

import SummaryCell from './SummaryCell';
import SeriesCell  from './SeriesCell';
import CanvasText  from './CanvasTextComponent';
import DrawText  from './TextComponent';

//console.log("Inside Table.")

//        flexGrow: 1,
//	

//	textOverflow: 'ellipsis',
//        textAlign: 'left',

const styles = theme => ({
    root: {
	height: '100%',
	maxWidth: '100%',
    },
    table: {
	padding: "0px",
	overflow: 'hidden',
	tableLayout: 'fixed',
	borderCollapse: "collapse",
    },
    dataCell : {
	cursor: "pointer",
	padding: theme.spacing.unit*0,
    },
    dataCellFill : {
	cursor: "pointer",
	padding: theme.spacing.unit*2,
	tableLayout: 'fixed',
	paddingTop: 0,
	paddingBottom: 0,
	verticalAlign: "top",
    },
    hdrCell : {
	cursor: "pointer",
	padding: theme.spacing.unit*0,
    },
    hdrCellEmpty : {
	padding: theme.spacing.unit*0,
    },
    hdrCellFill : {
	padding: theme.spacing.unit*2,
    },
    div : {
	margin:  0,
	verticalAlign:"top",
    },
});
function getTextWidth(txt, fontname, fontsize){
    if(getTextWidth.c === undefined){
        getTextWidth.c=document.createElement('canvas');
        getTextWidth.ctx=getTextWidth.c.getContext('2d');
    }
    if (fontname !== undefined) {
	getTextWidth.ctx.font = fontsize + ' ' + fontname;
    }
    return getTextWidth.ctx.measureText(txt).width;
};
function getTextHeight(fontname, fontsize){
    if(getTextHeight.c === undefined){
        getTextHeight.c=document.createElement('canvas');
        getTextHeight.ctx=getTextHeight.c.getContext('2d');
    }
    if (fontname !== undefined) {
	getTextHeight.ctx.font = fontsize + ' ' + fontname;
    }
    return getTextHeight.ctx.measureText('M').width;
}
function maxWidth(values,border) {
    var swidth=0;
    var mwidth=0;
    var lenv=values.length;
    for (var ii=0;ii<lenv;ii++) {
	var cwidth = getTextWidth(values[ii])+2*border;
	if (cwidth > mwidth) {
	    mwidth=cwidth;
	}
	swidth=swidth+cwidth;
    }
    return {max:mwidth,sum:swidth};
}
function makePlan(label,colvalues,rowvalues,iwidth,iheight,border) {
    var plan={rotate:false, step:1, head:100, cell:100, width:100, height:100, border:border, shift:0};
    if (iwidth <= 0) { return plan;};
    // text boundaries
    var wh=maxWidth(rowvalues,border);
    var ww=maxWidth(colvalues,border);
    var zwidth =getTextWidth(label) + 2 * border;   //props.theme.spacing.unit;
    var zheight=getTextHeight(label) + 2 * border;  //props.theme.spacing.unit;
    // table boundaries
    var mheight=getTextHeight() + 2 * border;       //props.theme.spacing.unit;
    var hwidth=Math.max(wh.max,zwidth) + mheight;
    var width=iwidth-hwidth;
    // calculate cell width...
    var mwidth=ww.max;
    var swidth=ww.sum;
    var lenc=colvalues.length;
    var lenr=rowvalues.length;
    if (mwidth*lenc < width) { // horisontal
	plan.rotate=false;
	plan.step=1;
	plan.width=width/lenc;
	plan.height=mheight;
	plan.shift=(plan.width-mwidth)/2;
	console.log("Plan (normal):",JSON.stringify(plan));
    } else if (mheight*lenc < width) { // rotate
	plan.rotate=true;
	plan.step=1;
	plan.width=width/lenc;
	plan.height=mwidth;
	plan.shift=(plan.width-mheight)/2;
	console.log("Plan (rot):",JSON.stringify(plan),lenc,plan.width*lenc);
    } else { // rotate and use steps
	plan.rotate=true;
	plan.step=Math.ceil(lenc*mheight/width);
	plan.width=plan.step*width/lenc;
	plan.height=mwidth;
	plan.shift=(plan.width-mheight)/2;
	console.log("Plan (rot+step):",JSON.stringify(plan),lenc,height,width);
    }
    // calculate cell height
    var height=iheight-plan.height;
    if (mheight*lenr < height) { // 
	plan.cell=Math.min(mheight*10,height/lenr);
    } else {
	plan.cell=mheight;
    }
    plan.cell=mheight;
    plan.head=hwidth;    
    console.log("Plan (finally):",JSON.stringify(plan),mheight,mwidth,height,width,lenr);
    return plan;
}
