import React, {Component} from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import Chip from '@material-ui/core/Chip';

import History  from './HistoryComponent';
import Settings from './SettingsComponent';

import SettingsIcon from '@material-ui/icons/Settings';
import UndoIcon from '@material-ui/icons/Undo';
import RedoIcon from '@material-ui/icons/Redo';

const styles = theme => ({
    config: {
        marginLeft: 'auto',
    },
    othchip: {
        margin: theme.spacing.unit,
	cursor: "pointer",
        color:"blue",
        borderColor:"blue",
    },
    trashchip: {
        margin: theme.spacing.unit,
	cursor: "pointer",
        color:"gray",
        borderColor:"gray",
  },
});
class Config extends Component {
    constructor(props) {
        super(props);
        const {state} = props;
        state.React.Config=this;
    };
    show(state) {
	this.forceUpdate();
    };
    state = {anchorSettings: null,};
    undoClick = event => {state.Navigate.undo(this.props.state);};
    redoClick = event => {state.Navigate.redo(this.props.state);};
    handleClick = event => {this.setState({ anchorSettings: event.currentTarget });};
    handleClose = () => {this.setState({ anchorSettings: null });};
    render() {
        const { classes, state } = this.props;
	const { anchorSettings } = this.state;
	return (
		<div className={classes.config}>
		 <Button
                    onClick={this.undoClick}
		   >
		   {<UndoIcon />}
                 </Button>
		 <Button
                    onClick={this.redoClick}
		   >
		   {<RedoIcon />}
                 </Button>
		 <Button
                    aria-owns={anchorSettings ? 'simple-menu' : undefined}
                    aria-haspopup="true"
                    onClick={this.handleClick}
		   >
		   {<SettingsIcon />}
                 </Button>
		<Settings classes={classes} state={state} anchor={anchorSettings} onclose={this.handleClose}/>
		</div>
	);
    }
}

Config.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Config);
