	//this.showButton(state,mFullscreen,true,false);
	this.setState();
    }
    render() {
        const { classes, state } = this.props;
	return (
		<div className={classes.mode}>
		Mode
		</div>
	);
    }
}

Mode.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Mode);
