import React, {Component} from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';

import SettingsIcon from '@material-ui/icons/Settings';
import HomeIcon from '@material-ui/icons/Home';

import Reload     from './ReloadComponent';
import View       from './ViewComponent';
import Mode       from './ModeComponent';
import KeyMenu    from './KeyMenuComponent';
import Order      from './OrderComponent';
import SelectMenu from './SelectMenuComponent';
import PriorityMenu from './PriorityMenuComponent';

const styles = theme => ({
    settings: {
        marginLeft: 'auto',
	color:'red',
    },
});
function Home(props) {
    const {state}=props;
    var onclick=() => {state.Navigate.setHome(state);state.Path.setFilter(state);};
    var title="Home";
    return <Button onClick={onclick} title={title}><HomeIcon/></Button>;
};
class Settings extends Component {
    state = {anchor: null,};
    render() {
        const { classes, state } = this.props;
	this.onClose = () => {this.setState({ anchor: null });console.log("Closing settings...");};
	this.onClick = (event) => {this.setState({ anchor: event.currentTarget });};
	return (<div>
		  <Button
                    aria-owns={this.state.anchor ? 'settings-menu' : undefined}
                    aria-haspopup="true"
                    onClick={this.onClick}
		    title={"Settings"}
		   >
		   {<SettingsIcon />}
                  </Button>
	          <Menu
                   id="settings-menu"
	           anchorEl={this.state.anchor}
                   open={Boolean(this.state.anchor)}
                   onClose={this.onClose}
	          >
		    <MenuItem key="reload" onClose={this.onClose}>
		       <Reload state={state}/>
		    </MenuItem>
		    <MenuItem key="view" onClose={this.onClose}>
		       <View state={state}/>
		    </MenuItem>
		    <MenuItem key="mode" onClose={this.onClose}>
		       <Mode state={state}/>
		    </MenuItem>
		    <MenuItem key="selects" onClose={this.onClose}>
		       <SelectMenu state={state}/>
		    </MenuItem>
		    <MenuItem key="order" onClose={this.onClose}>
		       <Order state={state}/>
		    </MenuItem>
		    <MenuItem key="keys" onClose={this.onClose}>
		       <KeyMenu state={state}/>
		    </MenuItem>
		    <MenuItem key="priorities" onClose={this.onClose}>
		       <PriorityMenu state={state}/>
		    </MenuItem>
		    <MenuItem key="home" onClose={this.onClose}>
		       <Home state={state}/>
		    </MenuItem>
	          </Menu>
		</div>
	       );
    }
}

Settings.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Settings);
