import React, {Component} from "react";
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';

import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';

import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableRow from '@material-ui/core/TableRow';
import TableHead from '@material-ui/core/TableHead';
import TableCell from '@material-ui/core/TableCell';
import ReactDOM from 'react-dom';

import SummaryCell from './SummaryCell';
import SeriesCell  from './SeriesCell';
import CanvasText  from './CanvasTextComponent';

//console.log("Inside Table.")

//        flexGrow: 1,
//	

const styles = theme => ({
    root: {
	height: '100%',
	maxWidth: '100%',
    },
    table: {
        textAlign: 'left',
	padding: "0px",
	borderStyle: 'solid',
	overflow: 'hidden',
    },
    row  :{},//	margin: '100px',
    cell : {
	cursor: "pointer",
	padding: theme.spacing.unit*0,
    },
    cleanCell : {
	padding: theme.spacing.unit*0,
    },
    firstCell : {
	padding: theme.spacing.unit*1,
	width: '100%',
	height:'100%',
    },
});
function getTextWidth(txt, fontname, fontsize){
    if(getTextWidth.c === undefined){
        getTextWidth.c=document.createElement('canvas');
        getTextWidth.ctx=getTextWidth.c.getContext('2d');
    }
    if (fontname !== undefined) {
	getTextWidth.ctx.font = fontsize + ' ' + fontname;
    }
    return getTextWidth.ctx.measureText(txt).width;
};
function getTextHeight(fontname, fontsize){
    if(getTextHeight.c === undefined){
        getTextHeight.c=document.createElement('canvas');
        getTextHeight.ctx=getTextHeight.c.getContext('2d');
    }
    if (fontname !== undefined) {
	getTextHeight.ctx.font = fontsize + ' ' + fontname;
    }
    return getTextHeight.ctx.measureText('M').width;
}
function makePlan(colvalues,rowvalues,width,height,border) {
    var plan={rotate:false, step:1, width:100, height:100,border:border};
    if (width <= 0) { return plan;};
    var height=getTextHeight()+2*border;
    var mwidth=0;
    var swidth=0;
    var lenc=colvalues.length;
    for (var ii=0;ii<lenc;ii++) {
	var cwidth = getTextWidth(colvalues[ii])+2*border;
	if (cwidth > mwidth) {
	    mwidth=cwidth;
	}
	swidth=swidth+cwidth;
    }
    if (mwidth*lenc < width) { // horisontal
	plan.rotate=false;
	plan.step=1;
	plan.width=width/lenc;
	plan.height=height;
    } else if (height*lenc < width) { // rotate
	plan.rotate=true;
	plan.step=1;
	plan.width=width/lenc;
	plan.height=mwidth;
    } else { // rotate and use steps
	plan.rotate=false;
	plan.step=Math.ceil(lenc*height/width);
	plan.width=plan.step*width/lenc;
	plan.height=mwidth;
    }
    return plan;
}
// ---------------- DATA
function FirstDataCell (props) {
    const { classes, rowval, onclick, title } = props;
    //console.log("Making first data cell.");
    return (<TableCell className={classes.firstCell} onClick={onclick} title={title}>{rowval}</TableCell>);
}
function DataCell(props) {
    const {classes,state,elements,mode,...other}=props;
    if (elements===undefined) {
	return <TableCell/>
    } else if (mode===state.Show.code.cell.Sum) {
	return <TableCell className={classes.cell}>YY</TableCell>;
	//return <SummaryCell {...other} state={state} elements={elements}/>
    } else {
	return <SeriesCell {...other} state={state} elements={elements}/>
    }
}
function renderDataCell(classes,state,colkey,colvalues,rowkey,rowval,rowindex,rowwhere,range,mode,plan,val,index) {
    var title=state.Database.getWhereDetail(colkey,colvalues[index]);
    //console.log("Making data cell:",rowval,val,index,plan,JSON.stringify(colvalues));
    if ((index-1)%plan.step === 0) {
	// get elements and range
	var elements=state.Matrix.getMatrixElements(colvalues,rowval,state.React.matrix,index,plan.step);
	// get count and colwhere
        var cnt = Math.min(colvalues.length,index+plan.step)-index;
        var colwhere = state.Database.getColWhere(colkey,colvalues,index,plan.step);
	// make onclick and title
	var onclick=() => state.Navigate.selectItem(state,colkey,rowkey,colvalues[index],rowval,colwhere,rowwhere,cnt,1);
	return (<DataCell className={classes.cell} classes={classes} state={state} key={`col-${index}`} index={index} onclick={onclick} title={title} 
		colkey={colkey} rowkey={rowkey} colvalues={colvalues} colval={val} rowval={rowval} colwhere={colwhere} rowwhere={rowwhere} 
		elements={elements} mode={mode} plan={plan} range={range}/>);
    } else {
	return null;
    }
}
function dataRow(classes,state,colkey,rowkey,colvalues,mode,plan,rowval,rowindex) {
    var rowwhere=state.Database.getWhereDetail(rowkey,rowval);
    var onclick=() => state.Navigate.selectKey(state,rowkey,rowval,rowwhere,1);
    var range=[undefined,undefined];
    if (state.React.matrix!==undefined) {
	range=state.Matrix.getRange(state,state.React.matrix,colvalues,[rowval]);
    };
    //console.log("Making data cols.",colkey,JSON.stringify(colvalues));
    //console.log("We have a matrix(",rowval,") with range:",JSON.stringify(range));
    var mapFunction= (val,index)=>renderDataCell(classes,state,colkey,colvalues,rowkey,rowval,rowindex,rowwhere,range,mode,plan,val,index);
    return (<TableRow key={rowindex.toString()}>
	    <FirstDataCell classes={classes} key={'0'} rowval={rowval} onclick={onclick} title={rowwhere}/>
	    {colvalues.map(mapFunction)}
	    </TableRow>);
};
function renderZeroRow(classes,state,colkey,colvalues,plan) {
    return (<TableRow key={'1'}>
	    <TableCell  key={`0`} state={state} index={'0'}>No data</TableCell>
	    </TableRow>);
};
function DataRows(props) {
    const { classes, state, plan, colkey, colvalues, rowkey, rowvalues, mode } = props;
    //console.log("Making data cols.",colkey,JSON.stringify(colvalues));
    var mapFunction= (val,index)=>dataRow(classes,state,colkey,rowkey,colvalues,mode,plan,val,index);
    if (rowvalues.length===0) {
	return renderZeroRow(classes,state,colkey,colvalues,plan);
    } else {
	return (rowvalues.map(mapFunction));
    }
}
// ---------------- HDR
function FirstHdrCell (props) {
    const { classes, colkey, rowkey } = props;
    var label=rowkey + "\\" + colkey;
    //console.log("Making first header cell.",label);
    return (<TableCell className={classes.firstCell}>{label}</TableCell>);
}
function renderHdrCell(classes,state,colkey,colvalues,rowkey,plan,val,index) {
    if ((index-1)%plan.step === 0) {
	//console.log("HdrCell:",index,plan.step);
        var cnt = Math.min(colvalues.length,index+plan.step)-index;
        var colwhere = state.Database.getColWhere(colkey,colvalues,index,plan.step);
	var onclick=() => state.Navigate.selectKey(state,colkey,colvalues[index],colwhere,cnt);
	var title=colwhere;
	var ref=React.createRef();
	//console.log("Ref:",JSON.stringify(ref));
	var cursor=classes.nopointer;
	if (onclick !== undefined) {
	    cursor=classes.pointer;
	}
	//var rotate=undefined;
	var rotate=1;
//[(onclick !== undefined?classes.pointer:classes.nopointer),
	return (
		<TableCell className={(onclick !== undefined?classes.cell:classes.cleanCell)} onClick={onclick} 
	                title={title} key={`col-${index}`} >
		   <CanvasText state={state} index={index} rotate={rotate} label={val}></CanvasText>
 	        </TableCell> );
