import React, {Component} from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import Chip from '@material-ui/core/Chip';
import Grid from "@material-ui/core/Grid/Grid";

import Settings from './config/SettingsComponent';

import UndoIcon from '@material-ui/icons/Undo';
import RedoIcon from '@material-ui/icons/Redo';
import HomeIcon from '@material-ui/icons/Home';

const styles = theme => ({
    config: {
        marginLeft: 'auto',
	width:'100%',
    },
    horisontal: {
        marginLeft: 'auto',
	display: 'flex',
	justifyContent: 'flex-end',
    },
});
function Undo(props) {
    const {state}=props;
    var onclick=() => state.Navigate.undo(state);
    var disundo=! state.Navigate.canUndo(state);
    var title="Undo";
    return <Button disabled={disundo} onClick={onclick} title={title}><UndoIcon/></Button>;
};
function Redo(props) {
    const {state}=props;
    var onclick=() => state.Navigate.redo(state);
    var disredo=! state.Navigate.canRedo(state);
    var title="Redo";
    return <Button disabled={disredo} onClick={onclick} title={title}><RedoIcon/></Button>;
};
function Home(props) {
    const {state}=props;
    var onclick=() => state.Navigate.goHome(state);
    var title="Home";
    return <Button onClick={onclick} title={title}><HomeIcon/></Button>;
};
function setTitle(title) {
};
class Config extends Component {
    constructor(props) {
        super(props);
        const {state} = props;
        state.React.Config=this;
    };
    show(state) {
	this.forceUpdate();
    };
    render() {
        const { classes, state } = this.props;
	var title=state.Path.getTitle(state);
	console.log("Title:",title);
	return (
		<div className={classes.config}>
		   <Grid container spacing={24}>
		      <Grid item xs={8} style={{color:'blue'}}>
		        <div className={classes.horisontal}>
           	         {title}
	                </div>
	              </Grid>
		      <Grid item xs={4}>
		        <div className={classes.horisontal}>
		         <Undo state={state}/>
                         <Redo state={state}/>
                         <Home state={state}/>
		         <Settings state={state}/>
		        </div>
		      </Grid>
		   </Grid>
		</div>
	);
    }
}

Config.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Config);
