import React, { Component} from 'react';
import {withStyles} from '@material-ui/core/styles';
import PropTypes from "prop-types";
import { SnackbarProvider, withSnackbar } from 'notistack';
// npm install notistack
const styles = theme => ({
    content: {
        flex: '1 0 auto',
        paddingTop: '5rem',
    }
});

function Details(props) {
    const {msg} = props; //state,
    return msg;
}

/**
 * The entire app get generated from this container.
 * We set the material UI theme by choosing a primary and secondary color from the metMuiThemes file
 * and creating a color palette with the createTheme method.
 * For information about using the different palettes see material UI documentation
 * 
 * This app contains the database, path and matrix states...
 */
class Status extends Component {
    constructor(props) {
	super(props);
	props.state.React.Status = this;
	this.state={msg:""};
    };
    // set dataset age
    setAge(state,age) {
	//console.log("Age...",state.Database.mod,age);
	this.setState();
    };
    setLog(state,msg) {
	//console.log("Updated...",msg,this.state.msg);
	this.setState({msg:msg});
	//this.forceUpdate();
    };
    progressInfo(state,e) {
	//console.log("progress...");
	this.setState({msg:"Progress"});
    };
    loadInfo(state,e) {
	//console.log("load...");
	this.setState({msg:"Loading"});
    };
    setInfo(state,msg) {
	//console.log("set...",msg);
	this.setState({msg:msg});
    };
    errorInfo(state,e) {
	//console.log("error...");
	this.setState({msg:"Error"});
    };
    abortInfo(state,e) {
	//console.log("abort...");
	this.setState({msg:"Abort"});
    };
    broadcast(msg,variant) { // variant could be 'success', 'error', 'warning' or 'info'
	if (variant === undefined) {variant='info';};
	this.props.enqueueSnackbar(msg, { variant });
    };
    render() {
        const { classes, state } = this.props;
        return (
                <div className={classes.content}>
		   <Details state={state} msg={this.state.msg}/>
                </div>
        );
    }
}

function IntegrationNotistack() {
  return (
    <SnackbarProvider maxSnack={10}>
      <Status />
    </SnackbarProvider>
  );
}
Status.propTypes = {
    classes: PropTypes.object.isRequired,
    enqueueSnackbar: PropTypes.func.isRequired,
};

export default withStyles(styles)(Status);
