import React, {Component} from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import Chip from '@material-ui/core/Chip';
import TrashIcon from '@material-ui/icons/Delete';
import CloseIcon from '@material-ui/icons/DeleteOutlined';
import KeyIcon from '@material-ui/icons/VpnKey';
import TabIcon from '@material-ui/icons/Apps';

import TableKey from './TableKeyComponent';

const styles = theme => ({
    settings:{},
    config: {
        marginLeft: 'auto',
    },
    othchip: {
        margin: theme.spacing.unit,
	cursor: "pointer",
        color:"blue",
        borderColor:"blue",
    },
    trashchip: {
        margin: theme.spacing.unit,
	cursor: "pointer",
        color:"gray",
        borderColor:"gray",
  },
});
function renderMenuItem(classes,state,keyitem,keyindex) {
    return (<MenuItem key={keyitem}>
	       <TableKey state={state} keyitem={keyitem}/> 
	    </MenuItem>);
}
class TableKeys extends Component {
    state={anchor:null};
    render() {
        const { classes, state, key, value } = this.props;
	this.onClick = event => {this.setState({ anchor: event.currentTarget });};
	this.onClose = () => {this.setState({ anchor: null });};
	var items=state.Path.trash.rest.concat(state.Path.other.rest);
	items=items.sort(state.Utils.ascending);
	var mapFunction= (item,index)=>renderMenuItem(classes,state,item,index);
	//console.log("TableKeys.rendering",items.length,JSON.stringify(anchor),Boolean(anchor));
	return (
		<div className={classes.view} key={key}>
		   <Chip
                      icon={<TabIcon />}
                      label={value}
                      aria-owns={this.state.anchor ? 'keys-menu' : undefined}
                      aria-haspopup="true"
                      onClick={this.onClick}
                      className={classes.tabchip}
                      variant="outlined"/>
		     <Menu
                        id="keys-menu"
	                anchorEl={this.state.anchor}
                        open={Boolean(this.state.anchor)}
                        onClose={this.onClose}
		     >
		        {items.map(mapFunction)}
	             </Menu>
		</div>
	);
    }
}

TableKeys.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(TableKeys);
