import React, {Component} from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import Chip from '@material-ui/core/Chip';
import Grid from "@material-ui/core/Grid/Grid";

import History  from './config/HistoryComponent';
import Settings from './config/SettingsComponent';

import UndoIcon from '@material-ui/icons/Undo';
import RedoIcon from '@material-ui/icons/Redo';

const styles = theme => ({
    config: {
        marginLeft: 'auto',
    },
});
function Undo(props) {
    const {state}=props;
    var onclick=() => state.Navigate.undo(state);
    var title="Undo";
    return <Button onClick={onclick} title={title}><UndoIcon/></Button>;
}
function Redo(props) {
    const {state}=props;
    var onclick=() => state.Navigate.redo(state);
    var title="Redo";
    return <Button onClick={onclick} title={title}><RedoIcon/></Button>;
}
class Config extends Component {
    constructor(props) {
        super(props);
        const {state} = props;
        state.React.Config=this;
    };
    show(state) {
	this.forceUpdate();
    };
    render() {
        const { classes, state } = this.props;
	return (
		<div className={classes.config}>
		   <Grid container spacing={24}>
		      <Grid item xs={8}>
		         <Undo state={state}/>
                         <Redo state={state}/>
		      </Grid>
	              <Grid item xs={4}>
		         <Settings state={state}/>
		      </Grid>
		   </Grid>
		</div>
	);
    }
}

Config.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Config);
