import React, {Component} from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import Chip from '@material-ui/core/Chip';

import ViewIcon from '@material-ui/icons/Visibility';
import NoViewIcon from '@material-ui/icons/VisibilityOff';
import KeyIcon from '@material-ui/icons/VpnKey';
import ChartIcon from '@material-ui/icons/BarChart';

import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemIcon from '@material-ui/core/ListItemIcon';
import ListItemText from '@material-ui/core/ListItemText';
import Subheader from '@material-ui/core/ListSubheader';
import Divider from '@material-ui/core/Divider';

const styles = theme => ({
    settings: {
        marginLeft: 'auto',
	color:'red',
    },
});
function ViewIconMode (props) {
    const {state} = props;
    var mode=state.Show.state.viewMode;
    if (mode === state.Show.code.view.path) {
	return (<ViewIcon/>);
    } else {
	return (<NoViewIcon/>);
    }
};
function setkey(event,state,el) {
    el.setState({ anchorKeys: event.currentTarget});
    console.log("Settings.keyclick after:",JSON.stringify(el.state));
}
function ViewButton(props) {
    const {classes, state, el, anchor}=props;
    var onclick=(event) => state.Show.toggleView(state);
    return (
	<div className={classes.config}>
	    <Button
               aria-owns={anchor ? 'view-menu' : undefined}
               aria-haspopup="true"
               onClick={onclick}
	    >
	       {<ViewIconMode state={state}/>}
            </Button>
	</div>
    );
};
function renderKeyItem(classes,state,keyitem,keyindex) {
    var tpos=state.Path.trash.rest.indexOf(keyitem);
    var onclick=() => state.Navigate.onClickPath(state,'trash',keyitem);
    var chip=(tpos!==-1 ? classes.trashchip : classes.othchip);
    var icon=null; //(tpos!==-1 ? <CloseIcon/> : null);
    //console.log("Keys.renderMenuItem",keyitem);
    return (<MenuItem key={keyitem}>
	     <Chip
	       icon={icon}
	       label={keyitem}
	       onClick={onclick}
	       className={chip}
	       variant="outlined"
	      />
	    </MenuItem>);
}
function KeysButton(props) {
    const {classes, state, el, onclose}=props;
    onclick = (event) => {el.setState({ anchorKeys: event.currentTarget})};
    var items=state.Path.trash.rest.concat(state.Path.other.rest);
    items=items.sort(state.Utils.ascending);
    var mapFunction= (item,index)=>renderKeyItem(classes,state,item,index);
    return (
	    <div className={classes.config}>
	       <div className={classes.config}>
	          <Button
                     aria-owns={el.state.anchorKeys ? 'keys-menu' : undefined}
                     aria-haspopup="true"
                     onClick={onclick}
	          >
	             {<KeyIcon />}
                  </Button>
	       </div>
	       <Menu
                  id="keys-menu"
	          anchorEl={el.state.anchorKeys}
                  open={Boolean(el.state.anchorKeys)}
                  onClose={onclose}
	         >
	          {items.map(mapFunction)}
	       </Menu>
	    </div>
    );
};
//	      
class Settings extends Component {
    state = {anchorView:null,anchorKeys: null,};
    handleClose = () => {this.setState({ anchorView: null, anchorKeys:null });};
    render() {
        const { classes, state, anchor, onclose } = this.props;
	const { anchorView,anchorKeys } = this.state;
	onclick = (event) => {this.setState({ anchorKeys: event.currentTarget})};
	var onClickView = (event) => state.Show.toggleView(state);
	var items=state.Path.trash.rest.concat(state.Path.other.rest);
	items=items.sort(state.Utils.ascending);
	var mapFunction= (item,index)=>renderKeyItem(classes,state,item,index);
	console.log("Settings.rendering:");
	return (
		<Menu id="settings-menu" value={this.props.value} onChange={this.props.change}  desktop={true} width={320}>
		test123
		<MenuItem value={1} primaryText="Text 1" />
		<MenuItem value={2}
	    primaryText="Cloud"
	    rightIcon={null}
	    menuItems={[
		    <MenuItem
		primaryText="Dev"
		rightIcon={null}
		menuItems={[
			<MenuItem value={3} primaryText="Spa" />,
			<MenuItem primaryText="Shorex"  />,
			<MenuItem primaryText="Shop" />,
			<MenuItem primaryText="Other"  />,
			<MenuItem primaryText="Entertainment"  />,
			<MenuItem primaryText="Activities"  />,
			<MenuItem primaryText="Shoreex"  />,
			<MenuItem primaryText="Dining"  />,
		]}
		    />,
		    <MenuItem
		primaryText="Test"
		rightIcon={null}
		menuItems={[
			<MenuItem primaryText="Spa" />,
			<MenuItem primaryText="Shorex"  />,
			<MenuItem primaryText="Shop" />,
			<MenuItem primaryText="Other"  />,
			<MenuItem primaryText="Entertainment"  />,
			<MenuItem primaryText="Activities"  />,
			<MenuItem primaryText="Shoreex"  />,
			<MenuItem primaryText="Dining"  />,
		]}
		    />]}
		/>
		</Menu>
	);
    }
}
//                      aria-owns={anchor ? 'view-menu' : undefined}
//                      aria-haspopup="true"
//		<ViewButton classes={classes} state={state} el={this} onclose={this.handleClose}/>

Settings.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Settings);
