import React, {Component} from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import Chip from '@material-ui/core/Chip';

import ViewIcon from '@material-ui/icons/Visibility';
import NoViewIcon from '@material-ui/icons/VisibilityOff';
import KeyIcon from '@material-ui/icons/VpnKey';
import ChartIcon from '@material-ui/icons/BarChart';

import Keys    from './KeysComponent';

const styles = theme => ({
    settings: {
        marginLeft: 'auto',
	color:'red',
    },
});
function ViewIconMode (props) {
    const {state} = props;
    var mode=state.Show.state.viewMode;
    if (mode === state.Show.code.view.path) {
	return (<ViewIcon/>);
    } else {
	return (<NoViewIcon/>);
    }
};
function setkey(event,state,el) {
    el.setState({ anchorKeys: event.currentTarget});
    console.log("Settings.keyclick after:",JSON.stringify(el.state));
}
function ViewButton(props) {
    const {classes, state, el, anchor}=props;
    var onclick=(event) => state.Show.toggleView(state);
    return (
	<div className={classes.config}>
	    <Button
               aria-owns={anchor ? 'simple-menu' : undefined}
               aria-haspopup="true"
               onClick={onclick}
	    >
	       {<ViewIconMode state={state}/>}
            </Button>
	</div>
    );
};
function KeysButton(props) {
    const {classes, state, anchor, onclick, onclose}=props;
    return (
	    <div className={classes.config}>
	       <Button
                  aria-owns={anchor ? 'simple-menu' : undefined}
                  aria-haspopup="true"
                  onClick={onclick}
	       >
	          {<KeyIcon />}
               </Button>
	    </div>
    );
};
class Settings extends Component {
    state = {anchorView:null,anchorKeys: null,};
    handleClick = (event) => {this.setState({ anchorKeys: event.currentTarget})};
    handleClose = () => {this.setState({ anchorView: null, anchorKeys:null });};
    render() {
        const { classes, state, anchor, onclose } = this.props;
	const { anchorView,anchorKeys } = this.state;
	//console.log("Settings.rendering:",JSON.stringify(this.state.anchorKeys));
	return (
		<Menu
	          className={classes.config}
                  id="simple-menu"
                  anchorEl={anchor}
                  open={Boolean(anchor)}
                  onClose={onclose}
		>
		<ViewButton classes={classes} state={state} anchor={this.anchorView} el={this} onclose={this.handleClose}/>
		<KeysButton classes={classes} state={state} anchor={this.anchorKeys} onclick={this.handleClick} onclose={this.handleClose}/>
		<Keys       classes={classes} state={state} anchor={this.anchorKeys} onclose={this.handleClose}/>
		</Menu>
	);
    }
}

Settings.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Settings);
