import React, {Component} from "react";
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import ReactTooltip from 'react-tooltip'
import Button from '@material-ui/core/Button';

import InfoIcon from '@material-ui/icons/Info';
import CancelIcon from '@material-ui/icons/Cancel';


const styles = theme => ({
    button: {
	color:'white',
	"&$buttonDisabled": {
            color: theme.palette.primary.main,
	},
    },
    buttonDisabled: {},
});
function Tooltip(props) {
    const {state,classes,rowkey,rowval,colkey,colvalues}=props;
    var onclick=() => {state.Matrix.addTooltip(state,data);this.update();}
    return (<div><h3>Rowkey: {data.rowkey} Colkey: {data.colkey}</h3>
	    <p>Put mouse here</p>
	    <Button className={classes.button} onClick={onclick}><InfoIcon/></Button></div>
	   );
};
class CellTooltip extends Component {
    update() {
	this.forceUpdate();
    };
    render() {
	const { classes, state } = this.props;
	//console.log("##### Rendering CellTooltip.");
	return (<ReactTooltip id='cell' 
		getContent={(dataTip) =>{if (dataTip==null) {
		    return null;
		} else {
		    const data=JSON.parse(dataTip);
		    //console.log("Tooltip:",JSON.stringify(dataTip),JSON.stringify(data));
		    return (<Tooltip state={state} classes={classes} 
			    rowkey={rowkey} rowval={rowval} 
			    colkey={colkey} colvalues={colvalues}/>
			   );
		}}}
		effect='solid'
		delayHide={500}
		delayShow={200}
		delayUpdate={500}
		place={'bottom'}
		border={true}
		type={'light'}
		/>);
	// {console.log("Datatip:",dataTip);
	//				  
    };
};
CellTooltip.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(CellTooltip);
