import React, {Component} from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';

import BarIcon from '@material-ui/icons/BarChart';

import FlagIcon from '@material-ui/icons/Flag';

const styles = theme => ({
    view: {
        marginLeft: 'auto',
    },
    button:{
	color:'white'
    },
});
function ModeIcon (props) {
    const {state} = props;
    var mode=state.Layout.state.cellMode;
    if (mode === state.Layout.code.cell.Sum) {
	return (<FlagIcon/>);
    } else {
	return (<BarIcon/>);
    }
};
class Mode extends Component {
    render() {
	const {classes, state}=this.props;
	var onclick = (event) => state.Layout.toggleMode(state);
	return (
		<div className={classes.view}>
		   <Button
                      className={classes.button}
                      onClick={onclick}
	              title={"Show mode"}
		    >
	  	       {<ModeIcon state={state}/>}
                    </Button>
		</div>
	);
    }
}

Mode.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Mode);
