import React, {Component} from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import Chip from '@material-ui/core/Chip';
import TrashIcon from '@material-ui/icons/Delete';
import CloseIcon from '@material-ui/icons/DeleteOutlined';

import History from './HistoryComponent';
import View    from './ViewComponent';
import Key     from './KeyComponent';

const styles = theme => ({
    settings:{},
    config: {
        marginLeft: 'auto',
    },
    othchip: {
        margin: theme.spacing.unit,
	cursor: "pointer",
        color:"blue",
        borderColor:"blue",
    },
    trashchip: {
        margin: theme.spacing.unit,
	cursor: "pointer",
        color:"gray",
        borderColor:"gray",
  },
});
function renderMenuItem(classes,state,keyitem,keyindex) {
    var tpos=state.Path.trash.rest.indexOf(keyitem);
    var onclick=() => state.Navigate.onClickPath(state,'trash',keyitem);
    var chip=(tpos!==-1 ? classes.trashchip : classes.othchip);
    var icon=null; //(tpos!==-1 ? <CloseIcon/> : null);
    //console.log("Keys.renderMenuItem",keyitem);
    return (<MenuItem key={keyitem}>
	     <Chip
	       icon={icon}
	       label={keyitem}
	       onClick={onclick}
	       className={chip}
	       variant="outlined"
	      />
	    </MenuItem>);
}
class Keys extends Component {
    state={anchor:null};
    handleClick = event => {this.setState({ anchor: event.currentTarget });};
    handleClose = () => {this.setState({ anchor: null });};
    render() {
        const { classes, state, anchor, onclose } = this.props;
	var items=state.Path.trash.rest.concat(state.Path.other.rest);
	items=items.sort(state.Utils.ascending);
	var mapFunction= (item,index)=>renderMenuItem(classes,state,item,index);
	//console.log("Keys.rendering",items.length,JSON.stringify(anchor),Boolean(anchor));
	return (<Menu
               id="keys-menu"
	       anchorEl={anchor}
               open={Boolean(anchor)}
               onClose={onclose}
	      >
	      <MenuItem justify={'center'} key={"close"} onClick={onclose}>
	         <CloseIcon/>
	      </MenuItem>
	   </Menu>
	);
    }
}
//               
//	      {items.map(mapFunction)}

Keys.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Keys);
