import React, {Component} from "react";
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';

import {teal_palette} from '../mui/metMuiThemes';

import { render } from 'react-dom';
import ReactGlobe from 'react-globe';

import markers from './markers';
import markerRenderer from './markerRenderer';

import './styles.css';
import texture from './globe_dark.jpg';

const footAndHeaderheight = "100px";

const styles = theme => ({
    content: {},
    root: {
	height: '100%',
	padding:0,
	margin:0,
	border: '5px solid red'
    },
    map: {
	overflow: 'hidden',
	height: '100%',
    },
});

function Map() {
  return (
    <div className="App">
      <ReactGlobe
        globeOptions={{
          texture:
            'https://raw.githubusercontent.com/chrisrzhou/react-globe/master/textures/globe_dark.jpg',
        }}
        markers={markers}
        markerOptions={{ renderer: markerRenderer }}
      />
    </div>
  );
}


class EarthMap extends Component {
    constructor(props) {
	super(props);
	const {state} = props;
	state.React.Map=this;
	this.bbx={height:0,width:0};
    };    
    handleClick() {
	this.refs.map.leafletElement.locate();
    }
    componentDidMount() {
        window.addEventListener("resize", this.updateWindowDimensions);
    } 
    updateWindowDimensions = () => {
        this.width= window.innerWidth;
	this.height=window.innerHeight;
	this.bbx=this.el.getBoundingClientRect();
	//console.log("Width/Height:",this.width,this.height,this.bbx.width,this.bbx.height)
    };
    element(el) {
	if (el !== undefined && el !== null) {
	    this.el=el;
	    this.bbx=this.el.getBoundingClientRect();
	    console.log("BBX width/height:",this.bbx.width,this.bbx.height);
	};
    };
  render() {
      const { classes, state } = this.props;
      return (<div ref={el=>{this.element(el)}}
	         className={classes.root}
	         style={{position:'fixed', width: '100%', height: 'calc(95% - '+footAndHeaderheight+')',overflow:'hidden'}} >
	         <Map className={classes.map}
                    center={[60, 10]}
                    zoom={3}
                    maxZoom={10}
                    attributionControl={true}
                    zoomControl={false}
                    doubleClickZoom={true}
                    scrollWheelZoom={true}
                    dragging={true}
                    animate={true}
                    easeLinearity={0.35} 
   	         />
	      </div>
	     );
  }
}

EarthMap.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(EarthMap);
