import React, {Component} from "react";
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import { StickyTable, Row, Cell } from 'react-sticky-table';
import 'react-sticky-table/dist/react-sticky-table.css';

import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography/Typography";

import TableCell from '@material-ui/core/TableCell';
import TableSortLabel from '@material-ui/core/TableSortLabel';

console.log("Inside Table.")

const styles = theme => ({
    root: {
        flexGrow: 1,
    },
    paper: {
        padding: theme.spacing.unit * 2,
        textAlign: 'left',
    },
    table: {
        textAlign: 'center',
        padding: theme.spacing.unit * 2,
	cursor: "pointer",
	height: '100%',
	width: '100%',
    },
    cell : {cursor: "pointer",},
});

function onCellClick(event) {
    var rowid=event.target.getAttribute("rowid");
    event.target.innerHTML = rowid;
}

function GetRows(props) {
    const { classes, state } = props;
    var rows = [];
    for (var i=0; i<10; i++) {
	rows.push((<Row><Cell>{i} - </Cell><Cell rowid={i} onClick={onCellClick}>Click Me!</Cell></Row>));
    }
    return {rows};
}

function Details(props) {
   return <div style={{width: '100%', height: '100%'}}>
           <StickyTable>
	      <Row><Cell>1 - </Cell><Cell rowid={1} onClick={onCellClick}>Click Me!</Cell></Row>
	      <Row><Cell>2 - </Cell><Cell rowid={2} onClick={onCellClick}>Click Me!</Cell></Row>
	      <Row><Cell>2 - </Cell><Cell rowid={3} onClick={onCellClick}>Click Me!</Cell></Row>
	      <Row><Cell>2 - </Cell><Cell rowid={4} onClick={onCellClick}>Click Me!</Cell></Row>
	      <Row><Cell>2 - </Cell><Cell rowid={5} onClick={onCellClick}>Click Me!</Cell></Row>
	      <Row><Cell>2 - </Cell><Cell rowid={6} onClick={onCellClick}>Click Me!</Cell></Row>
	      <Row><Cell>2 - </Cell><Cell rowid={7} onClick={onCellClick}>Click Me!</Cell></Row>
	      <Row><Cell>2 - </Cell><Cell rowid={8} onClick={onCellClick}>Click Me!</Cell></Row>
	      <Row><Cell>2 - </Cell><Cell rowid={9} onClick={onCellClick}>Click Me!</Cell></Row>
	      <Row><Cell>2 - </Cell><Cell rowid={18} onClick={onCellClick}>Click Me!</Cell></Row>
	      <Row><Cell>2 - </Cell><Cell rowid={28} onClick={onCellClick}>Click Me!</Cell></Row>
	      <Row><Cell>2 - </Cell><Cell rowid={38} onClick={onCellClick}>Click Me!</Cell></Row>
	      <Row><Cell>2 - </Cell><Cell rowid={48} onClick={onCellClick}>Click Me!</Cell></Row>
	      <Row><Cell>2 - </Cell><Cell rowid={58} onClick={onCellClick}>Click Me!</Cell></Row>
	      <Row><Cell>2 - </Cell><Cell rowid={68} onClick={onCellClick}>Click Me!</Cell></Row>
	      <Row><Cell>2 - </Cell><Cell rowid={78} onClick={onCellClick}>Click Me!</Cell></Row>
           </StickyTable>
          </div>
 }

class Table extends Component {
    constructor(props) {
	super(props);
	this.state=GetRows(props);
    }
    componentDidMount() {} 
    render() {
    const { classes, state } = this.props;
    return (
        <div className={classes.root}>
            <Grid container spacing={24}>
               <Grid item xs={12}> 
                  { <Paper className={classes.table}>
		    <Details state={state} classes={classes}/>
                    </Paper>}
               </Grid>
            </Grid>
	</div>
    );
  }
}

Table.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Table);
