import React, {Component} from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Typography from "@material-ui/core/Typography/Typography";
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';

const styles = theme => ({
    root: {
        backgroundColor: theme.palette.primary.main,
        marginTop: theme.spacing.unit * 8,
        bottom: 0,
        padding: `${theme.spacing.unit * 6}px 0`,
        color: '#FFF'
    },
    text: {
        maxWidth: theme.spacing.getMaxWidth.maxWidth,
        margin: theme.spacing.getMaxWidth.margin,
    },
});

class Config extends Component {
    constructor(props) {
        super(props);
        const {state} = props;
        state.React.Config=this;
    };
    showMatrix(state,matrix) {
	state.React.matrix=matrix;
	this.setState();
    }
    state = {anchorEl: null,};
    handleClick = event => {this.setState({ anchorEl: event.currentTarget });};
    handleClose = () => {this.setState({ anchorEl: null });};
    render() {
        const { classes, state } = this.props;
	const { anchorEl } = this.state;
	return (
		<div>
		<Button
            aria-owns={anchorEl ? 'simple-menu' : undefined}
            aria-haspopup="true"
            onClick={this.handleClick}
	    className="float-right"
		>
		Config
            </Button>
		<Menu
            id="simple-menu"
            anchorEl={anchorEl}
            open={Boolean(anchorEl)}
            onClose={this.handleClose}
		>
		<MenuItem onClick={this.handleClose}>Config 1</MenuItem>
		<MenuItem onClick={this.handleClose}>Config 2</MenuItem>
		<MenuItem onClick={this.handleClose}>Config 3</MenuItem>
		</Menu>
		</div>
	);
    }
}

Config.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Config);
