import React, {Component} from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Grid from "@material-ui/core/Grid/Grid";

import Settings from './SettingsComponent';

import UndoIcon from '@material-ui/icons/Undo';
import RedoIcon from '@material-ui/icons/Redo';
import HomeIcon from '@material-ui/icons/Home';
import BarIcon from '@material-ui/icons/BarChart';
import FlagIcon from '@material-ui/icons/Flag';

const styles = theme => ({
    config: {
        marginLeft: 'auto',
	width:'100%',
    },
    horisontal: {
        marginLeft: 'auto',
	display: 'flex',
	justifyContent: 'flex-end',
	height:'100%',
	alignItems:'center',
    },
});
function Undo(props) {
    const {state}=props;
    var onclick=() => state.Navigate.undo(state);
    var disundo=! state.Navigate.canUndo(state);
    var title="Undo";
    return <Button disabled={disundo} onClick={onclick} title={title}><UndoIcon/></Button>;
};
function Redo(props) {
    const {state}=props;
    var onclick=() => state.Navigate.redo(state);
    var disredo=! state.Navigate.canRedo(state);
    var title="Redo";
    return <Button disabled={disredo} onClick={onclick} title={title}><RedoIcon/></Button>;
};
function Home(props) {
    const {state}=props;
    var onclick=() => state.Path.goHome(state);
    var title="Home";
    return <Button style={{color:'white'}} onClick={onclick} title={title}><HomeIcon/></Button>;
};
function Mode(props) {
    const {state}=props;
    var onclick=() => state.Show.toggleMode(state);
    var title="Mode";
    var mode=state.Show.state.cellMode;
    if (mode === state.Show.code.cell.Sum) {
	return <Button style={{color:'white'}} onClick={onclick} title={title}><FlagIcon/></Button>;
    } else {
	return <Button style={{color:'white'}} onClick={onclick} title={title}><BarIcon/></Button>;
    }
};
class Config extends Component {
    constructor(props) {
        super(props);
        const {state} = props;
        state.React.Config=this;
    };
    show(state) {
	//console.log("Called Config.show...");
	this.forceUpdate();
    };
    render() {
        const { classes, state } = this.props;
	var title=state.Path.getTitle(state);
	//console.log("Rendering Config...");
	return (
		<div className={classes.config}>
		   <Grid container spacing={24}>
		      <Grid item xs={8} style={{color:'white'}}>
		        <div className={classes.horisontal}>
           	         {title}
	                </div>
	              </Grid>
		      <Grid item xs={4}>
		        <div className={classes.horisontal}>
		         <Undo state={state}/>
                         <Redo state={state}/>
                         <Home state={state}/>
		         <Mode state={state}/>
                         <Settings state={state}/>
		        </div>
		      </Grid>
		   </Grid>
		</div>
	);
    }
}

Config.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Config);
