import React, {Component} from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Chip from '@material-ui/core/Chip';
import PriorityIcon from '@material-ui/icons/VerticalAlignTop';

const styles = theme => ({
    priority: {
	display: 'inline-block',
        marginRight: 'auto',
    },
    othchip: {
        margin: theme.spacing.unit,
	cursor: "pointer",
        color:"blue",
        borderColor:"blue",
    },
    trashchip: {
        margin: theme.spacing.unit,
	cursor: "pointer",
        color:"gray",
        borderColor:"gray",
  },
});
class Priority extends Component {
    render() {
        const { classes, state, priorityitem } = this.props;
	var tpos=state.Path.trash.rest.indexOf(priorityitem);
	var onclick=() => {state.Layout.increasePriority(state,priorityitem);this.forceUpdate()};
	var chip=(tpos!==-1 ? classes.trashchip : classes.othchip);
	var icon=null; //(tpos!==-1 ? <CloseIcon/> : null);
	return (
		<div className={classes.priority}>
	 	   <Chip
	              icon={null}
	              label={priorityitem}
	              onClick={onclick}
	              className={chip}
	              variant="outlined"
		   />
		</div>
	);
    }
}

Priority.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Priority);
