import React, {Component} from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';

import SettingsIcon from '@material-ui/icons/Settings';

import View       from './ViewComponent';
import Keys       from './KeysComponent';
import TableOrder     from './TableOrderComponent';
import Selects    from './SelectsComponent';
import Priorities from './PrioritiesComponent';

const styles = theme => ({
    settings: {
        marginLeft: 'auto',
	color:'red',
    },
});
class Settings extends Component {
    state = {anchor: null,};
    handleClose = () => {this.setState({ anchorView: null, anchorKeys:null });};
    render() {
        const { classes, state } = this.props;
	const { anchorView,anchorKeys } = this.state;
	this.handleClose = () => {this.setState({ anchor: null });console.log("Closing");};
	this.handleClick = (event) => {this.setState({ anchor: event.currentTarget });};
	return (<div>
		  <Button
                    aria-owns={this.state.anchor ? 'settings-menu' : undefined}
                    aria-haspopup="true"
                    onClick={this.handleClick}
		    title={"Settings"}
		   >
		   {<SettingsIcon />}
                  </Button>
	          <Menu
                   id="settings-menu"
	           anchorEl={this.state.anchor}
                   open={Boolean(this.state.anchor)}
                   onClose={this.handleClose}
	          >
		    <MenuItem key="view" onClose={this.handleClose}>
		       <View classes={classes} state={state}/>
		    </MenuItem>
		    <MenuItem key="keys" onClose={this.handleClose}>
		       <Keys classes={classes} state={state}/>
		    </MenuItem>
		    <MenuItem key="values" onClose={this.handleClose}>
		       <TableOrder classes={classes} state={state}/>
		    </MenuItem>
		    <MenuItem key="values" onClose={this.handleClose}>
		       <Selects classes={classes} state={state}/>
		    </MenuItem>
		    <MenuItem key="values" onClose={this.handleClose}>
		       <Priorities classes={classes} state={state}/>
		    </MenuItem>
	          </Menu>
		</div>
	       );
    }
}

Settings.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Settings);
