import React, {Component} from "react";
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Grid from '@material-ui/core/Grid';
import Chip from '@material-ui/core/Chip';
import SelIcon from '@material-ui/icons/Eject';
import TabIcon from '@material-ui/icons/Apps';
//import TrashIcon from '@material-ui/icons/Delete';

console.log("Inside PathComponent.")

const styles = theme => ({
    root: {
        flexGrow: 1,
    },
    paper: {
        padding: theme.spacing.unit * 2,
        textAlign: 'left',
    },
    paperImage: {
        textAlign: 'center',
        padding: theme.spacing.unit * 2,
    },
    selchip: {
        margin: theme.spacing.unit,
	cursor: "pointer",
        color:"red",
        borderColor:"blue",
    },
    tabchip: {
        margin: theme.spacing.unit,
	cursor: "pointer",
        color:"green",
        borderColor:"blue",
    },
    othchip: {
        margin: theme.spacing.unit,
	cursor: "pointer",
        color:"blue",
        borderColor:"blue",
  },
});
// SELECT PATH
function SelectChip(props) {
    const { classes, value } = props;//state, key, index, onclick, title, 
    return <Chip
           icon={<SelIcon />}
           label={value}
           onClick={handleClick}
           className={classes.selchip}
           variant="outlined"
	/>
}
function renderSelectPath(classes,state,item,index) {
    var key=state.Path.keys.path[index];
    var val=state.Path.select.val[key];
    var onclick="Navigate.onClickPath(state,'path','"+index+"');";
    var title="'"+state.Path.select.where[key]+"'";
    return <SelectChip classes={classes} state={state} key={`select-${key}`} index={index} onclick={onclick} title={title} value={val}/>;
}
function SelectPath(props) {
    const { classes, state } = props;
    var items=state.Path.keys.path;
    var mapFunction= (item,index)=>renderSelectPath(classes,state,item,index);
    return items.map(mapFunction);
}
// TABLE PATH
function TableChip(props) {
    const { classes, state, key, index, onclick, title, value } = props;
    return <Chip
           icon={<TabIcon />}
           label={value}
           onClick={handleClick}
           className={classes.tabchip}
           variant="outlined"
	/>
}
function renderTablePath(classes,state,item,index) {
    var key=state.Path.other.table[index];
    var onclick="Navigate.onClickPath(state,'table','"+index+"');";
    var title="'"+state.Path.other.table[index]+"'";
    return <TableChip classes={classes} state={state} key={`table-${key}`} index={index} onclick={onclick} title={title} value={key}/>;
}
function TablePath(props) {
    const { classes, state } = props;
    var items=state.Path.other.table;
    var mapFunction= (item,index)=>renderTablePath(classes,state,item,index);
    return items.map(mapFunction);
}
// OTHER PATH
function OtherChip(props) {
    const { classes, state, onclick, title, value } = props;
    return <Chip
           label={value}
           onClick={onclick}
           className={classes.othchip}
           variant="outlined"
	/>
}
function renderOtherPath(classes,state,item,index) {
    var key=state.Path.other.rest[index];
    var onclick="Navigate.onClickPath(state,'rest','"+index+"');";
    var title="'"+state.Path.other.rest[index]+"'";
    return <OtherChip classes={classes} state={state} key={`other-${key}`} index={index} onclick={onclick} title={title} value={key}/>;
}
function OtherPath(props) {
    const { classes, state } = props;
    var items=state.Path.other.rest;
    var mapFunction= (item,index)=>renderOtherPath(classes,state,item,index);
    return items.map(mapFunction);
}
//    this.showTrash=function(state) {
///	var item=document.getElementById("trashRest");
//	state.Html.removeChildren(item);
//	if (this.state.qtrash  === 1) {
//	    var lab;
//	    var rel="";
//	    var title="";
//	    // path
//	    var rlen = state.Path.trash.rest.length;
//	    for (var ii = 0; ii < rlen; ii++) {
//		var key=state.Path.trash.rest[ii];
//		var val=state.Path.select.val[key];
//		if (state.Path.keys.path.indexOf(key) !== -1) { val=undefined;};
//		state.Html.addPathElement(state,item,"trashRest",key,val,
//				    "Navigate.onClickPath(state,'trash','"+ii+"');",
//				    "Trashed '"+state.Path.trash.rest[ii]+"'");
//	    }
//	}
//
function Details(props) {
//  items=[{value:"X",index:1}]
//  {keys.map((value, index) => (<SelectChip key={`item-${index}`} index={index} value={value} />))}
    const { classes, state } = props;
    return <div style={{width: '100%'}}>
	   <SelectPath classes={classes} state={state}/>
	   <TablePath  classes={classes} state={state}/>
	   <OtherPath  classes={classes} state={state}/>
        </div>
}
function onCellClick(event) {
    var rowid=event.target.getAttribute("rowid");
    event.target.innerHTML = rowid;
}
function handleDelete() {
  alert('You clicked the delete icon.'); // eslint-disable-line no-alert
}

function handleClick() {
  alert('You clicked the Chip.'); // eslint-disable-line no-alert
}

class PathComponent extends Component {
    constructor(props) {
	super(props);
	const {state} = props;
	state.React.Path=this;
    };
    showPath() {
	console.log("Showing path.");
	this.forceUpdate();
    };
    showTrash() {
	this.setState();
    };
    render() {
        const { classes, state } = this.props;
        return (
            <div className={classes.root}>
                <Grid container spacing={24}>
                    <Grid item xs={12}>
		       <Details classes={classes} state={state}/>
                    </Grid>
                </Grid>
            </div>
        );
    }
}

PathComponent.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(PathComponent);
