import React, {Component} from "react";
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';

import {teal_palette} from '../mui/metMuiThemes';

import ReactGlobe from 'react-globe';

import defmarkers from './markers';
import markerRenderer from './markerRenderer';

import './styles.css';

const footAndHeaderheight = "100px";

function getTooltipContent(marker) {
  return `${marker.city} (Value: ${marker.value})`;
}

const styles = theme => ({
    content: {},
    root: {
	height: '100%',
	padding:0,
	margin:0,
	border: '5px solid red'
    },
    dataset: {
	overflow: 'hidden',
	height: '100%',
    },
    map: {
	overflow: 'hidden',
	height: '100%',
    },
});

class EarthMap extends Component {
    constructor(props) {
	super(props);
	const {state} = props;
	state.React.Map=this;
	this.markers=defmarkers;
    };
    element(el) {
	if (el !== undefined && el !== null) {
	    this.el=el;
	};
    };
    show(state) {
	// get marker data
	// make markers
	// set markers
    };
//    <div className="App">
    onClickMarker=function(marker, markerObject, event) {
	console.log("Clicked marker...",marker.id)
	// setEvent({
	//   type: 'CLICK',
	//   marker,
	//   markerObjectID: markerObject.uuid,
	//   pointerEventPosition: { x: event.clientX, y: event.clientY },
	// });
	//setDetails(getTooltipContent(marker));
    };
    onDefocus=function (previousCoordinates, event){
	//setEvent({
	//  type: 'DEFOCUS',
	//  previousCoordinates,
	//  pointerEventPosition: { x: event.clientX, y: event.clientY },
	//});
	//setDetails(null);
    };
    render() {
	const { classes, state } = this.props;
	return (<div ref={el=>{this.element(el)}}
	        className={classes.root}
	        style={{position:'fixed', marginLeft:'0%',width: '90%', height: 'calc(95% - '+footAndHeaderheight+')',overflow:'hidden'}} >
	           <ReactGlobe 
                    markers={this.markers}
                    focus={[65,15]} 
                    zoom={2} 
                    initialCoordinates={[65,20]}
                    onClickMarker={this.onClickMarker}
                    markerOptions={{renderer: markerRenderer,
                                     getTooltipContent:getTooltipContent,  
                                   }}
                   focusOptions={{
                   animationDuration: 1000, 
                   distanceRadiusScale: 1.75,
                   easingFunction: ['Bounce', 'InOut'],
                  }}
                  onDefocus={this.onDefocus}
	        />
	      </div>
	     );
    }
}

EarthMap.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(EarthMap);
