import React, {Component} from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import Chip from '@material-ui/core/Chip';
import TableOrderIcon from '@material-ui/icons/Apps';
import Values from './ValuesComponent';

const styles = theme => ({
    settings:{},
    tableorders: {
	display: 'inline-block',
        marginRight: 'auto',
    },
    othchip: {
        margin: theme.spacing.unit,
	cursor: "pointer",
        color:"blue",
        btableorderColor:"blue",
    },
    trashchip: {
        margin: theme.spacing.unit,
	cursor: "pointer",
        color:"gray",
        btableorderColor:"gray",
  },
});
function renderMenuItem(classes,state,keyitem,keyindex) {
    return (<MenuItem key={keyitem} className={classes.tableorders}>
	    <Values classes={classes} state={state} keyitem={keyitem}/> 
	    </MenuItem>);
}
class TableOrder extends Component {
    state={anchor:null};
    handleClick = event => {this.setState({ anchor: event.currentTarget });};
    handleClose = () => {this.setState({ anchor: null });};
    render() {
        const { classes, state } = this.props;
	var items=state.Path.other.table;
	items=items.sort(state.Utils.ascending);
	var mapFunction= (item,index)=>renderMenuItem(classes,state,item,index);
	//console.log("TableOrder.rendering",items.length,JSON.stringify(anchor),Boolean(anchor));
	return (
		<div className={classes.tableorders}>
		   <Button
                      aria-owns={this.state.anchor ? 'tableorders-menu' : undefined}
                      aria-haspopup="true"
                      onClick={this.handleClick}
	              title={"Value Order"}
		    >
	  	       {<TableOrderIcon state={state}/>}
                     </Button>
		     <Menu
	                className={classes.tableorders}
                        id="tableorders-menu"
	                anchorEl={this.state.anchor}
                        open={Boolean(this.state.anchor)}
                        onClose={this.handleClose}
		     >
		        {items.map(mapFunction)}
	             </Menu>
		</div>
	);
    }
}

TableOrder.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(TableOrder);
