import React, {Component} from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import Chip from '@material-ui/core/Chip';
import SelectIcon from '@material-ui/icons/Star';
import SelectValueMenu from './SelectValueMenuComponent';

const styles = theme => ({
    selects: {
	display: 'inline-block',
        marginRight: 'auto',
	height:'100%',
    },
    tableselects: {
	display: 'inline-block',
        marginRight: 'auto',
    },
});
function renderMenuItem(classes,state,keyitem,keyindex) {
    return (<MenuItem className={classes.selects} key={keyitem}>
	    <SelectValueMenu state={state} keyitem={keyitem}/>
	    </MenuItem>
	   );
}
class SelectMenu extends Component {
    state={anchor:null};
    render() {
        const { classes, state } = this.props;
	this.onClick = event => {this.setState({ anchor: event.currentTarget });};
	this.onClose = () => {this.setState({ anchor: null });};
	var items=state.Path.keys.path;
	//items=items.sort(state.Utils.ascending);
	var mapFunction= (item,index)=>renderMenuItem(classes,state,item,index);
	//console.log("Selects.rendering",items.length,JSON.stringify(anchor),Boolean(anchor));
	return (
		<div className={classes.tableselects}>
		   <Button
                      aria-owns={this.state.anchor ? 'selects-menu' : undefined}
                      aria-haspopup="true"
                      onClick={this.onClick}
	              title={"Selected"}
		    >
	  	       {<SelectIcon state={state}/>}
                     </Button>
		     <Menu
	                className={classes.tableselects}
                        id="selects-menu"
	                anchorEl={this.state.anchor}
                        open={Boolean(this.state.anchor)}
                        onClose={this.onClose}
		     >
		        {items.map(mapFunction)}
	             </Menu>
		</div>
	);
    }
}

SelectMenu.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(SelectMenu);
