import React, {Component} from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Chip from '@material-ui/core/Chip';
import ValueIcon from '@material-ui/icons/SwapVert';

const styles = theme => ({
    value: {
	display: 'inline-block',
        marginRight: 'auto',
    },
    othchip: {
        margin: theme.spacing.unit,
	cursor: "pointer",
        color:"blue",
        borderColor:"blue",
    },
    trashchip: {
        margin: theme.spacing.unit,
	cursor: "pointer",
        color:"gray",
        borderColor:"gray",
  },
});
class Value extends Component {
    render() {
        const { classes, state, valueitem } = this.props;
	var tpos=state.Path.trash.rest.indexOf(valueitem);
	var onclick=() => state.Navigate.onClickPath(state,'trash',valueitem);
	var chip=(tpos!==-1 ? classes.trashchip : classes.othchip);
	var icon=null; //(tpos!==-1 ? <CloseIcon/> : null);
	return (
		<div className={classes.value}>
		   <Chip
	              icon={<ValueIcon/>}
	              label={valueitem}
	              onClick={onclick}
	              className={chip}
	              variant="outlined"
		   />
		</div>
	);
    }
}

Value.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Value);
