import React, {Component} from "react";
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';

import {teal_palette} from '../mui/metMuiThemes';

import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';

import SummaryCell from './SummaryCell';
import SeriesCell  from './SeriesCell';
import CanvasText  from './CanvasTextComponent';

const styles = theme => ({
    root: {
	height: '100%',
    },
    paper: {
	overflow: 'hidden',
	tableLayout: 'fixed',
	padding:0,
	margin:0,
    },
    divTable :{
	display: 'table',
	width: '100%',
    },
    divTableRow:  {
	display: 'table-row',
    },
    divTableCell:{
	border: '0px solid #999999',
	display: 'table-cell',
	padding: '0px 0px',
    },
    divTableCellCursor:{
	cursor: "pointer",
	border: '0px solid #999999',
	display: 'table-cell',
	padding: '0px 0px',
    },
    divTableHead : {
	border: '0px solid #999999',
	display: 'table-cell',
	padding: '0px 0px',
    },
    divTableHeading : {
	display: 'table-header-group',
    },
    divTableHeadingCenter : {
	display: 'flex',
	justifyContent: 'center',
    },
    divTableFoot : {
	backgroundColor: '#DDD',
	display: 'table-footer-group',
	fontWeight: 'bold',
    },
    divTableBody : {
	display: 'table-row-group',
    }
});

//const mui = createTheme({palette:teal_palette});

function getTextWidth(txt, fontname, fontsize){
    if(getTextWidth.c === undefined){
        getTextWidth.c=document.createElement('canvas');
        getTextWidth.ctx=getTextWidth.c.getContext('2d');
    }
    if (fontname !== undefined) {
	getTextWidth.ctx.font = fontsize + ' ' + fontname;
    }
    return getTextWidth.ctx.measureText(txt).width;
};
function getTextHeight(fontname, fontsize){
    if(getTextHeight.c === undefined){
        getTextHeight.c=document.createElement('canvas');
        getTextHeight.ctx=getTextHeight.c.getContext('2d');
    }
    if (fontname !== undefined) {
	getTextHeight.ctx.font = fontsize + ' ' + fontname;
    }
    return getTextHeight.ctx.measureText('M').width;
}
function maxWidth(values,border) {
    var swidth=0;
    var mwidth=0;
    var lenv=values.length;
    for (var ii=0;ii<lenv;ii++) {
	var cwidth = getTextWidth(values[ii])+2*border;
	if (cwidth > mwidth) {
	    mwidth=cwidth;
	}
	swidth=swidth+cwidth;
    }
    return {max:mwidth,sum:swidth};
}
function makePlan(label,colvalues,rowvalues,iwidth,iheight,border) {
    var plan={cell:{width:100,height:100,rotate:false,step:1,xoffset:0,yoffset:0,border:border}, 
	      hdr:{width:100,height:100,rotate:false,step:1,xoffset:0,yoffset:0,border:border},
	      row:{width:100,height:100,rotate:false,step:1,xoffset:0,yoffset:0,border:border},
	      col:{width:100,height:100,rotate:false,step:1,xoffset:0,yoffset:0,border:border}};
    if (iwidth <= 0) { return plan;};
    // text boundaries
    var wh=maxWidth(rowvalues,border);
    var ww=maxWidth(colvalues,border);
    var zwidth =getTextWidth(label) + 2 * border;   //props.theme.spacing.unit;
    // var zheight=getTextHeight(label) + 2 * border;  //props.theme.spacing.unit;
    // table boundaries
    var mheight=getTextHeight() + 2 * border;       //props.theme.spacing.unit;
    var hwidth=Math.max(wh.max,zwidth) + mheight;
    var width=iwidth-hwidth;
    // calculate cell width...
    var mwidth=ww.max;
    //var swidth=ww.sum;
    var lenc=colvalues.length;
    var lenr=rowvalues.length;
    if (mwidth*lenc < width) { // horisontal
	plan.hdr.rotate=false;
	plan.step=1;
	plan.cell.width=width/lenc;
	plan.hdr.height=mheight;
	plan.hdr.xoffset=(plan.cell.width-mwidth)/2;
	//console.log("Plan (normal):",JSON.stringify(plan));
    } else if (mheight*lenc < width) { // rotate hdr
	plan.hdr.rotate=true;
	plan.step=1;
	plan.cell.width=width/lenc;
	plan.hdr.height=mwidth;
	plan.hdr.xoffset=(plan.cell.width-mheight)/2;
	//console.log("Plan (rot):",JSON.stringify(plan),lenc,plan.cell.width*lenc);
    } else { // rotate hdr and use steps
	plan.hdr.rotate=true;
	plan.step=Math.ceil(lenc*mheight/width);
	plan.cell.width=plan.step*width/lenc;
	plan.hdr.height=mwidth;
	plan.hdr.xoffset=(plan.cell.width-mheight)/2;
	//console.log("Plan (rot+step):",JSON.stringify(plan),lenc,height,width);
    }
    // calculate cell height
    var height=iheight-plan.hdr.height;
    if (mheight*lenr < height) { // 
	plan.cell.height=Math.min(mheight*10,height/lenr);
    } else {
	plan.cell.height=mheight;
    }
    plan.hdr.width=hwidth;    
    //console.log("Plan (finally):",JSON.stringify(plan),mheight,mwidth,height,width,lenr);
    return plan;
}
