//console.log("Loading ShowLib.js");
function Show() {
  this.code = {
    mSum: 0,
    // cell mode
    mSeries: 1,
    // cell mode
    mItem: 2,
    // cell mode
    mTable: 0,
    // layout mode
    mMap: 1 // layout mode

  };
  this.state = {
    qtrash: 0,
    // should we show trash contents?
    cellMode: 0,
    // sum, series, item
    layoutMode: 0 // table, map

  };

  this.init = function (state) {
    var par = "Show" + state.Utils.version;
    state.Utils.init(par, this);
  };

  this.getDim = function (state) {
    var colkey = state.Path.getColKey(state) || "";
    var rowkey = state.Path.getRowKey(state) || "";

    if (colkey !== "" && rowkey !== "") {
      return 2;
    } else if (colkey !== "" || rowkey !== "") {
      return 1;
    } else {
      return 0;
    }
  };

  this.setLayoutMode = function (state, mode) {
    var om = this.state.layoutMode;
    var o = this.getLayoutMode(state);
    this.state.layoutMode = mode;
    var n = this.getLayoutMode(state); //console.log("Setting layout mode:",mode,":",o,"->",n);

    if (o !== n) {
      this.showAll(state);
    } else if (om !== mode) {
      this.showMode(state);
    }
  };

  this.setCellMode = function (state, mode) {
    var om = this.state.cellMode;
    var o = this.getCellMode(state);
    this.state.cellMode = mode;
    var n = this.getCellMode(state); //console.log("Setting cell mode:",mode,":",o,"->",n);

    if (o !== n) {
      this.showAll(state);
    } else if (om !== mode) {
      this.showMode(state);
    }
  };

  this.getLayoutMode = function (state) {
    //console.log("Getmode init:",this.state.layoutMode,state.Matrix.cnt);
    var mode = this.state.layoutMode;

    if (mode === this.code.mMap && state.Matrix.cnt > state.Matrix.popSeries) {
      mode = this.code.mTable;
    }

    return mode;
  };

  this.getCellMode = function (state) {
    var mode = this.state.cellMode;

    if (mode === this.code.mSingle && state.Matrix.cnt > state.Matrix.popSingle) {
      mode = this.code.mSeries;
    }

    if (mode === this.code.mSeries && state.Matrix.cnt > state.Matrix.popSeries) {
      mode = this.code.mSum;
    }

    return mode;
  };

  this.showMode = function (state) {
    // show data on screen
    var mSum = document.getElementById("modeSum");
    var mSeries = document.getElementById("modeSeries");
    var mSingle = document.getElementById("modeSingle");
    var mTable = document.getElementById("modeTable");
    var mMap = document.getElementById("modeMap");
    var layoutMode = this.getLayoutMode(state);
    var cellMode = this.getCellMode(state);
    this.showButton(state, mSum, cellMode === this.code.mSum, this.state.cellMode === this.code.mSum);
    this.showButton(state, mSeries, cellMode === this.code.mSeries, this.state.cellMode === this.code.mSeries);
    this.showButton(state, mSingle, cellMode === this.code.mSingle, this.state.cellMode === this.code.mSingle);
    this.showButton(state, mTable, layoutMode === this.code.mTable, this.state.layoutMode === this.code.mTable);
    this.showButton(state, mMap, layoutMode === this.code.mMap, this.state.layoutMode === this.code.mMap);
    var mTrash = document.getElementById("trash");
    var mFullscreen = document.getElementById("fullscreen");
    this.showButton(state, mTrash, true, false);
    this.showButton(state, mFullscreen, true, false);
  };

  this.showButton = function (state, item, main, sel) {
    if (main && sel) {
      item.setAttribute("style", state.Html.getButtonStyle(state.Colors.colors.table, state.Colors.colors.fg, true));
    } else if (main && !sel) {
      item.setAttribute("style", state.Html.getButtonStyle(state.Colors.colors.table, state.Colors.colors.fg, false));
    } else if (!main && sel) {
      item.setAttribute("style", state.Html.getButtonStyle(state.Colors.colors.rest, state.Colors.colors.fg, true));
    } else if (!main && !sel) {
      item.setAttribute("style", state.Html.getButtonStyle(state.Colors.colors.rest, state.Colors.colors.fg, false));
    }
  };

  this.showAll = function (state, reload) {
    // show data on screen
    //var documentLog = document.getElementById("log");
    state.Html.setLog(state, "Processing");
    console.log("this.Showing data.");

    if (reload === undefined || reload) {
      state.Database.dbextract(state, function (matrix) {
        // callback
        //console.log("Updating matrix.");
        console.log("Showing path");
        this.showPath(state);
        console.log("Showing trash");
        this.showTrash(state);
        console.log("Showing matrix");
        this.showMatrix(state, matrix);
        console.log("Pushing URL");
        state.Utils.pushUrl(state);
      }.bind(this));
    } else {
      console.log("Not updating matrix.");
      state.Path.exportAllKeys(state);
      this.showPath(state);
      this.showTrash(state);
    }

    this.showMode(state);
    state.Html.setLog(state);
  };

  this.showPath = function (state) {
    if (state.React.Path !== undefined) {
      state.React.Path.showPath(); // forceUpdate()
    } else {
      console.log("No react-path available.");
    }
  };

  this.showTrash = function (state) {
    if (state.React.Path !== undefined) {
      state.React.Path.showTrash(); // forceUpdate()
    } else {
      console.log("No react-trash available.");
    }
  };

  this.showMatrix = function (state, matrix) {
    var colkey = state.Path.getColKey(state) || "";
    var rowkey = state.Path.getRowKey(state) || "";
    var colvalues = [""];
    var rowvalues = [""];
    var item = document.getElementById("matrix");
    var skeys = state.Matrix.sortedKeys(state, state.Matrix.keyCnt);
    var layoutMode = this.getLayoutMode(state);
    var cellMode = this.getCellMode(state);
    var dim = this.getDim(state);
    state.Html.removeChildren(item);

    if (layoutMode === this.code.mMap) {
      var colvalues = state.Path.filterKeys(state, state.Matrix.values[colkey] || [""]);
      var rowvalues = state.Path.filterKeys(state, state.Matrix.values[rowkey] || [""]);
      state.Map2D.Show(state, matrix, item, skeys, colkey, colvalues, rowkey, rowvalues, cellMode);
    } else if (dim === 0) {
      console.log("0D:", layoutMode, cellMode, colkey, rowkey, this.code.mSum, this.code.mSeries);
      state.Table0D.Show(state, matrix, item, skeys, colkey, colvalues, rowkey, rowvalues, cellMode);
    } else if (dim === 1) {
      var colvalues = state.Path.filterKeys(state, state.Matrix.values[colkey] || [""]);
      state.Table1D.Show(state, matrix, item, skeys, colkey, colvalues, cellMode);
    } else if (dim === 2) {
      var colvalues = state.Path.filterKeys(state, state.Matrix.values[colkey] || [""]);
      var rowvalues = state.Path.filterKeys(state, state.Matrix.values[rowkey] || [""]);
      state.Table2D.Show(state, matrix, item, skeys, colkey, colvalues, rowkey, rowvalues, cellMode);
    }

    state.Html.rotateHeight(state);
  };

  this.useCanvas = function (state, matrix) {
    // check if matrix elements have max 1 data
    // loop over matrix
    for (var ii in matrix) {
      for (var jj in matrix[ii]) {
        if (matrix[ii][jj].cnt > 1) {
          return false;
        }
      }
    }

    return true;
  };

  this.scale = function (xval, xmin, xmax, ymin, ymax) {
    if (ymin > ymax) {
      return ymin + (xval - xmin) * (ymax - ymin) / (xmax - xmin);
    } else {
      return (xval - xmin) * (ymax - ymin) / (xmax - xmin);
    }
  };
}

;
