import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import { Cell } from 'react-sticky-table';


const styles = theme => ({
    pointer: {
	cursor:"pointer"
    },
    nopointer: {
    },
});
function SummaryCell(props) {
    const { state,key,index,onclick,
	    colwhere,rowwhere,
	    elements } = props;
    var cnt=0;
    var lev=-1;
    var elen=elements.length;
    for (var ee=0;ee<elen;ee++) {
        cnt=cnt+elements[ee].cnt;
        lev=Math.max(lev,elements[ee].lev);
    }; 
    var label="";
    if (cnt > 1) {label=cnt;};
    var bgcolor=state.Colors.getLevelColor(lev);
    var classname="nopointer";
    if (onclick !== undefined) {
	classname="pointer";
    }
    var title="";
    if (colwhere !== "" && rowwhere !== "") {
	title="("+colwhere+") AND ("+rowwhere+")";
    } else if (colwhere !== "") {
	title=colwhere;
    } else if (rowwhere !== "") {
	title=rowwhere;
    }
    return (
         <Cell className={classname} bgcolor={bgcolor} onclick={onclick} title={title} key={key}>{label}</Cell>
    );
}

SummaryCell.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(SummaryCell);
