import React, {Component} from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import Chip from '@material-ui/core/Chip';
import TrashIcon from '@material-ui/icons/Delete';
import CloseIcon from '@material-ui/icons/DeleteOutlined';
import KeyIcon from '@material-ui/icons/VpnKey';

import History from './HistoryComponent';
import View    from './ViewComponent';
import Key     from './KeyComponent';

const styles = theme => ({
    settings:{},
    config: {
        marginLeft: 'auto',
    },
    othchip: {
        margin: theme.spacing.unit,
	cursor: "pointer",
        color:"blue",
        borderColor:"blue",
    },
    trashchip: {
        margin: theme.spacing.unit,
	cursor: "pointer",
        color:"gray",
        borderColor:"gray",
  },
});
function renderMenuItem(classes,state,keyitem,keyindex) {
    return (<MenuItem key={keyitem}>
	       <Key classes={classes} state={state} keyitem={keyitem}/> 
	    </MenuItem>);
}
class Keys extends Component {
    state={anchor:null};
    handleClick = event => {this.setState({ anchor: event.currentTarget });};
    handleClose = () => {this.setState({ anchor: null });};
    render() {
        const { classes, state } = this.props;
	var items=state.Path.trash.rest.concat(state.Path.other.rest);
	items=items.sort(state.Utils.ascending);
	var mapFunction= (item,index)=>renderMenuItem(classes,state,item,index);
	//console.log("Keys.rendering",items.length,JSON.stringify(anchor),Boolean(anchor));
	return (
		<div className={classes.view}>
		   <Button
                      aria-owns={this.state.anchor ? 'keys-menu' : undefined}
                      aria-haspopup="true"
                      onClick={this.handleClick}
	              title={"Keys"}
		    >
	  	       {<KeyIcon state={state}/>}
                     </Button>
		     <Menu
                        id="keys-menu"
	                anchorEl={this.state.anchor}
                        open={Boolean(this.state.anchor)}
                        onClose={this.handleClose}
		     >
		        {items.map(mapFunction)}
	             </Menu>
		</div>
	);
    }
}

Keys.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Keys);
