import React, {Component} from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';

import EditLevelIcon from '@material-ui/icons/EditLevel';
import HomeIcon from '@material-ui/icons/Home';
import FontIcon from '@material-ui/icons/TextFields';

import { CompactPicker as Picker } from 'react-color';

const styles = theme => ({
    settings: {
        marginRight: 'auto',
	color:'red',
    },
    button:{color:'white'},
    divTableCell:{
	border: '0px solid #999999',
	display: 'table-cell',
	padding: '0px',
//	border:  '1px solid blue',
    },
});
class LevelColor extends Component {
    state = {anchor: null,};
    render() {
        const { state,classes,bg,fg,level } = this.props;
	//console.log("Rendering LevelColor...");
	this.onClose = () => {this.setState({ anchor: null });};
	this.onClick = (event) => {this.setState({ anchor: event.currentTarget });};
	const handleColorChange = ({ hex }) => {state.Colors.setLevelBgColor(this.state.level,hex);state.Show.show(state,false);}
	style={color:fg,backgroundColor:bg,textAlign:'center'};
	return (<div>
		  <div classname={classes.divTableCell} style={style}
                    aria-owns={this.state.anchor ? 'settings-menu' : undefined}
                    aria-haspopup="true"
                    onClick={this.onClick}
		   >
		      {level}
		  </div>
	          <Menu
		   settings={{float:'right'}}
                   id="settings-menu"
	           anchorEl={this.state.anchor}
                   open={Boolean(this.state.anchor)}
                   onClose={this.onClose}
	          >
		    <MenuItem key="reload" onClose={this.onClose}>
		        <Picker color={bg} onChangeComplete={ handleColorChange }
			/>
		    </MenuItem>
	          </Menu>
		</div>
	       );
    }
}

LevelColor.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(LevelColor);
