import React, {Component} from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import Chip from '@material-ui/core/Chip';

import History  from './HistoryComponent';
import Settings from './SettingsComponent';

import UndoIcon from '@material-ui/icons/Undo';
import RedoIcon from '@material-ui/icons/Redo';

const styles = theme => ({
    config: {
        marginLeft: 'auto',
    },
    othchip: {
        margin: theme.spacing.unit,
	cursor: "pointer",
        color:"blue",
        borderColor:"blue",
    },
    trashchip: {
        margin: theme.spacing.unit,
	cursor: "pointer",
        color:"gray",
        borderColor:"gray",
  },
});
function Undo(props) {
    const {state}=props;
    var onclick=() => state.Navigate.undo(state);
    var title="Undo";
    return <Button onClick={onclick} title={title}><UndoIcon/></Button>;
}
function Redo(props) {
    const {state}=props;
    var onclick=() => state.Navigate.redo(state);
    var title="Redo";
    return <Button onClick={onclick} title={title}><RedoIcon/></Button>;
}
class Config extends Component {
    constructor(props) {
        super(props);
        const {state} = props;
        state.React.Config=this;
    };
    show(state) {
	this.forceUpdate();
    };
    render() {
        const { classes, state } = this.props;
	const { anchorSettings } = this.state;
	return (
		<div className={classes.config}>
		 <Undo state={state}/>
                 <Redo state={state}/>
		 <Settings classes={classes} state={state}/>
		</div>
	);
    }
}

Config.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Config);
