import React, {Component} from "react";
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import ReactTooltip from 'react-tooltip'
import Button from '@material-ui/core/Button';

import InfoIcon from '@material-ui/icons/Info';
import CancelIcon from '@material-ui/icons/Cancel';


const styles = theme => ({
    button: {
	color:'white',
	"&$buttonDisabled": {
            color: theme.palette.primary.main,
	},
    },
    buttonDisabled: {},
});


class CellTooltip extends Component {
    update() {
	this.forceUpdate();
    };
    render() {
	const { classes, state } = this.props;
	//console.log("##### Rendering CellTooltip.");
	return (<ReactTooltip id='cell' 
		getContent={(data) =>{if (data==null) {
		    return null;
		} else {
		    const d=JSON.parse(data);
		    console.log("Tooltip:",JSON.stringify(data),JSON.stringify(d));
		    var onclick=() => state.Show.showTooltip(state,d.rowkey,d.colkey,this);
		    return (<div><h3>Rowkey: {d.rowkey} Colkey: {d.colkey}</h3>
			    <p>Put mouse here</p>
			    <Button onClick={onclick}><InfoIcon/></Button></div>);
		};
				     }
			   }
		effect='solid'
		delayHide={500}
		delayShow={200}
		delayUpdate={500}
		place={'bottom'}
		border={true}
		type={'light'}
		/>);
	// {console.log("Datatip:",dataTip);
	//				  
    };
};
CellTooltip.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(CellTooltip);
