import React, {Component, useState} from "react";
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';

import {teal_palette} from '../mui/metMuiThemes';

import { render } from 'react-dom';
import ReactGlobe from 'react-globe';

import defmarkers from './markers';
import markerRenderer from './markerRenderer';

import './styles.css';
import texture from './globe_dark.jpg';

const footAndHeaderheight = "100px";

function getTooltipContent(marker) {
  return `CITY: ${marker.city} (Value: ${marker.value})`;
}

const styles = theme => ({
    content: {},
    root: {
	height: '100%',
	padding:0,
	margin:0,
	border: '5px solid red'
    },
    dataset: {
	overflow: 'hidden',
	height: '100%',
    },
    map: {
	overflow: 'hidden',
	height: '100%',
    },
});

function Map(props) {
  const { classes, state, markers } = props;
  console.log("rendering map...");
//  const [markers, setMarkers] = useState([]);
  const [event, setEvent] = useState(null);
  const [details, setDetails] = useState(null);
  function onClickMarker(marker, markerObject, event) {
    console.log("Clicked marker...",marker.id)
    setEvent({
      type: 'CLICK',
      marker,
      markerObjectID: markerObject.uuid,
      pointerEventPosition: { x: event.clientX, y: event.clientY },
    });
    setDetails(getTooltipContent(marker));
  }
  function onDefocus(previousCoordinates, event) {
    setEvent({
      type: 'DEFOCUS',
      previousCoordinates,
      pointerEventPosition: { x: event.clientX, y: event.clientY },
    });
    setDetails(null);
  }
  return (
    <div className="App">
      <ReactGlobe 
         markers={markers}
         onClickMarker={onClickMarker}
         focus={[65,15]} 
         zoom={6} 
         initialCoordinates={[65,15]}
         markerOptions={{ renderer: markerRenderer}}
         focusOptions={{
          animationDuration: 3000,
          distanceRadiusScale: 2,
          easingFunction: ['Elastic', 'In'],
        }}
      />
    </div>
  );
}

class EarthMap extends Component {
    constructor(props) {
	super(props);
	const {state} = props;
	state.React.Map=this;
    };    
    element(el) {
	if (el !== undefined && el !== null) {
	    this.el=el;
	};
    };
  render() {
      const { classes, state } = this.props;
      return (<div ref={el=>{this.element(el)}}
	         className={classes.root}
	         style={{position:'fixed', marginLeft:'0%',width: '90%', height: 'calc(95% - '+footAndHeaderheight+')',overflow:'hidden'}} >
	         <Map state={state} markers={defmarkers}/>
	      </div>
	     );
  }
}

EarthMap.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(EarthMap);
