import React, {Component} from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import DownloadIcon from '@material-ui/icons/CloudDownload';

const styles = theme => ({
    load: {
	width: '100%',
    },
});

function Upload(props) {
    const {state,classes,onclick}=props;
    var title="Download setup";
    return <Button className={classes.button} onClick={onclick} title={title}><DownloadIcon/></Button>;
};

class Load extends Component {
    render() {
	const { classes, state } = this.props;
	let fileReader;
	let file;
	var onclick=() => {
	    fileReader = new FileReader();
	    fileReader.onloadend = handleFileRead;
	    fileReader.readAsText(file);
	};
	const handleFileRead = (e) => {
	    const content = fileReader.result;
	    console.log("Read:",content);
	}
	const handleFileChosen = (trg) => {
	    file=trg;
	}
	return (
		<div className={classes.load}>
   	  	   <input type='file' id='file'
	              onChange={e=>handleFileChosen(e.target.files[0])}/>
		   <Upload state={state} onClick={onclick} classes={classes}/>
		</div>
	);
    }
}

Load.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Load);
