import React, { Component} from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';

const styles = theme => ({
    pointer: {
	cursor:"pointer",
	padding: theme.spacing.unit*0,
    },
    nopointer: {
	padding: theme.spacing.unit*0,
    },
});

function updateCanvas(item) {
    const {state,elements,colkey,rowkey,colvalues,index,range,label,rotate,width} = item.props;
    const cnv=item.refs.text;
    const ctx = cnv.getContext('2d');
    var theight = cnv.height;
    var height=ctx.measureText('M').width;
    ctx.save();
    //ctx.translate(newx, newy);
    //if (rotate !== undefined) {ctx.rotate(-Math.PI/2);};
    ctx.textAlign = "center"; //left right center
    //ctx.font = "40px Courier"
    ctx.fillText(label, -100, -100); // labelXposition
    ctx.restore();
    console.log("Label:",label,width,height);
    //console.log("Text:",cnv.width,cnv.height);
}


class TextComponent extends React.Component {
    constructor(props) {
        super(props);
        const {state} = props;
    };
    componentDidMount() {
        updateCanvas(this);
    }
    render() {
        const { classes, onclick, title, ...other } = this.props;
	var cursor=classes.nopointer;
	if (onclick !== undefined) {
	    cursor=classes.pointer;
	}
        return (
		<canvas {...other} className={cursor} classes={classes} onClick={onclick} title={title} ref="text"/>
        );
    }
}

TextComponent.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(TextComponent);
